async function save_employee_weekly_off(organization_id, additions, db, ho_id) {
  let currentdatetime = customFormatDate(new Date())['date_formatted'];

  for (let weekDay in additions) {
    for (let employee_id of additions[weekDay]) {
      const selectQuery = `
        SELECT * FROM employee_shift_details
        WHERE shift_id = 0
        AND organization_id = ${organization_id}
        AND day = ${weekDay}
        AND employee_id = ${employee_id}
        AND start_time IS null AND end_time is null
      `;

      const existingRecord = await execQuery(selectQuery, db);

      if (existingRecord.length > 0) {
        const updateQuery = `
          UPDATE employee_shift_details
          SET status = 1, is_weekoff = 1, modified = '${currentdatetime}'
          WHERE ho_id = ${ho_id}
          AND shift_id = 0
          AND organization_id = ${organization_id}
          AND day = ${weekDay}
          AND employee_id = ${employee_id};
        `;

        await execQuery(updateQuery, db);
      } else {

        const insertQuery = `
            INSERT INTO employee_shift_details 
            (shift_id, ho_id, organization_id, day, employee_id, is_weekoff, modified, created)
            VALUES (?, ?, ?, ?, ?, ?, ?, ?)
        `;

        const values = [
            0,
            ho_id,
            organization_id,
            weekDay,
            employee_id,
            1,
            currentdatetime,
            currentdatetime
        ];

        const formattedQuery = mysql.format(insertQuery, values);
        await execQuery(formattedQuery, db);
      }
    }
  }
}

