async function handlePublishLog(requestBody, oldData, newData, requestDetails, metaData) {
  try {
    const {
      ho_id,
      organization_id = 0,
      user_id,
      username
    } = requestBody;

    const publishPayload = {
      ho_id,
      branch_id: organization_id,
      user_id,
      user_name: username,
      old_value: safeStringify(oldData),
      new_value: safeStringify(newData),
      request_details: safeStringify(requestDetails),
      request_from: requestBody.device_type === "web" ? "Web App" : "Mobile App",
      module_type: "Shift management",
      ...metaData
    };

    if (oldData === null) delete publishPayload.old_value;
    if (newData === null) delete publishPayload.new_value;

    console.log('Publishing log:', publishPayload);
    await publishLogLayer.publish(publishPayload, 'Shift');

  } catch (error) {
    console.error('Error publishing log for shift', error);
  }
}

function safeStringify(data) {
  try {
    return JSON.stringify(data);
  } catch (err) {
    console.warn('Could not stringify data:', err);
    return null;
  }
}

async function insertLogForShiftShiftAddOrEdit(requestBody, requestDetails, action) {
  const { shift_name, shift_daywise_data = [], sub_module_id, shift_description } = requestBody;

  const dayNames = {
    1: "Sunday",
    2: "Monday",
    3: "Tuesday",
    4: "Wednesday",
    5: "Thursday",
    6: "Friday",
    7: "Saturday",
  };

  const operationMap = {
    1: { method: "created", type: "INSERT" },
    2: { method: "updated", type: "UPDATE" },
    3: { method: "deleted", type: "DELETE" },
  };

  const { method: operationMethod, type: operationType } = operationMap[action] || {};

  const baseMessage = `Shift ${shift_name} ${operationMethod}`;

  const daywiseDescriptions = shift_daywise_data?.map((shift) => {
    const dayName = dayNames[shift?.day] || `Day ${shift?.day}`;
    return `- ${dayName} (Start Time: ${convertToAmPm(shift?.from_time)}, End Time: ${convertToAmPm(shift?.to_time)})`;
  });

  // Combine all parts of the description
  const descriptionParts = [baseMessage, ...daywiseDescriptions];

  // Append remark if provided
  if (shift_description && shift_description.trim() !== "") {
    descriptionParts.push(`Remark: ${shift_description.trim()}`);
  }

  const logDescription = descriptionParts.join("\n");
  const newValue = shift_daywise_data;
  const oldValue = requestBody?.old_value;

  const logMetadata = {
    employee_id: null,
    employee_name: null,
    sub_module_type: "Shift",
    sub_module_id: requestBody.sub_module_id,
    operation: operationType,
    log_title: `Shift ${operationMethod}`,
    log_descriptions: logDescription,
  };

  await handlePublishLog(requestBody, oldValue, newValue, requestDetails, logMetadata);
}

function convertToAmPm(time24) {
  if (typeof time24 !== "string" || !time24.includes(":")) {
    return ""; // or return "Invalid time" or null, based on your use case
  }

  const [hourStr, minuteStr] = time24.split(":");

  const hour = parseInt(hourStr, 10);
  const minute = minuteStr?.padStart(2, "0") || "00";

  if (isNaN(hour)) {
    return ""; // or handle as invalid input
  }

  const ampm = hour >= 12 ? "PM" : "AM";
  const convertedHour = hour % 12 || 12;

  return `${convertedHour}:${minute} ${ampm}`;
}


async function getShiftOldData(request_body, db) {

  let qry = mysql.format(`
    SELECT 
    s.id,
    s.name,
    s.description,
    s.default_shift,
    JSON_ARRAYAGG(
          JSON_OBJECT(
            'day', sd.day,
            'from_time', IFNULL(DATE_FORMAT(sd.start_time, "%h:%i %p"), null),
            'to_time', IFNULL(DATE_FORMAT(sd.end_time, "%h:%i %p"), null)
          )
        ) AS shift_daywise_data
      FROM 
      shifts s
      LEFT JOIN 
      shift_details sd ON sd.ho_id = ? and s.id = sd.shift_id
      WHERE 
      s.id = ?
      AND s.ho_id = ?
    `,[request_body.ho_id, request_body.shift_id, request_body.ho_id]);

    let old_shift_data = await execQuery(qry, db);
  return old_shift_data.length > 0 ? old_shift_data : null;
}

