  //   const shiftDays = getShift.shift_daywise_data !== undefined ? JSON.parse(getShift.shift_daywise_data) : [];
  //   const getDay = shiftDays.find(shiftDay => parseInt(shiftDay.day) === parseInt(day));
  //   return getDay;
  // }, [shifts])

  // const isDefaultWeekoff = (shiftDayData) => {  
