  const [openDropdownId, setOpenDropdownId] = useState(null);
  const [selectedTagsDropdownValue, setSelectedTagsDropdownValue] = useState(null);

  const handleDropdownClick = useCallback((popupId) => {
    if (openDropdownId === popupId) {
