                          <span className="me-1">
                            <img src={triwarning} alt="" className="img-fluid" />
                          </span>Occasional Week Off
                        </p>
                      </div>
                    </div>
                  </div>

                  <div>

                    <div className="table-responsive w-100">

                      <Col xl={12}>
                        {!isEmployeeWeekOffFetching ? (
                          <MyCustomTable
                            columns={columns}
                            data={employees}
                            getRowProps={(row) => ({
                              className: row.original.isPrimary ? 'ps-dashed-border-bottom' : ''
