import {
  API_RESPONSE_SUCCESS,
  API_RESPONSE_ERROR,
  GET_SHIFTWISE_EMPLOYEES,
  SAVE_EMPLOYEES_SHIFT_MAPPING,
  AFTER_SAVE_MAPPING,
  AFTER_SHIFT_EMP_LOADED,
  GET_SHIFT_DESIG_GROUP,
  AFTER_SHIFT_GROUP_LOADED,
  GET_EMPLOYEE_DESIGNATIONS,
  SAVE_STAFF_REQUIREMENT_REQUEST,
  SAVE_STAFF_REQUIREMENT_FAILURE,
  SAVE_STAFF_REQUIREMENT_SUCCESS,
  GET_STAFF_REQUIREMENT,
  AFTER_SHIFT_DAY_WISE_STAFF_LOADED,
  AFTER_SAVED_STAFF_REQUIREMENT_DATA,
  ASSIGN_SHIFT_REQUEST,
  AFTER_SAVE_ASSIGN_SHIFT,
  GET_EMP_WITH_SHIFT_REQUEST,
  GET_EMP_WITH_WEEKOFF_REQUEST,
  SAVE_EMPLOYEE_WEEKOFF_REQUEST,
  AFTER_SAVE_EMPLOYEE_WEEKOFF,
} from "./actionTypes";

// common success
export const empShiftsApiResponseSuccess = (actionType, data) => ({
  type: API_RESPONSE_SUCCESS,
  payload: { actionType, data },
});
// common error
export const empShiftsApiResponseError = (actionType, error) => ({
  type: API_RESPONSE_ERROR,
  payload: { actionType, error },
});

//new grid actions
export const getShiftwiseEmployees = day => ({
  type: GET_SHIFTWISE_EMPLOYEES,
  payload: day
});

export const saveEmployeesShiftMapping = ({day, additions, deletions, addWeekoff, removeWeekoff, assignedShifts, defaultShiftId, employeeNames}) => ({
  type: SAVE_EMPLOYEES_SHIFT_MAPPING,
  payload: {day, additions, deletions, addWeekoff, removeWeekoff, assignedShifts, defaultShiftId, employeeNames}
});

export const afterSaveMapping = () => ({
  type: AFTER_SAVE_MAPPING,
});

export const afterShiftEmpLoaded = () => ({
  type: AFTER_SHIFT_EMP_LOADED,
})

export const getShiftDesignationGroup = day => ({
  type: GET_SHIFT_DESIG_GROUP,
  payload: day
});

export const afterShiftGroupLoaded = () => ({
  type: AFTER_SHIFT_GROUP_LOADED,
});

export const getEmployeeDesignations = () => ({
  type: GET_EMPLOYEE_DESIGNATIONS
});

export const saveStaffRequirementRequest = data => ({
  type: SAVE_STAFF_REQUIREMENT_REQUEST,
  payload: data,
});

export const saveStaffRequirementSuccess = (actionType, data) => ({
  type: SAVE_STAFF_REQUIREMENT_SUCCESS,
  payload: { actionType, data },
});

export const saveStaffRequirementFailure = (actionType, error)  => ({
  type: SAVE_STAFF_REQUIREMENT_FAILURE,
  payload: { actionType, error },
});

export const getStaffRequirementRequest = (day, shiftId) => ({
  type: GET_STAFF_REQUIREMENT,
  payload: {day, shiftId}
});

export const afterDayWiseStaffReqLoaded = () => ({
  type: AFTER_SHIFT_DAY_WISE_STAFF_LOADED,
});

export const afterSaveStaffReqData = data => ({
  type: AFTER_SAVED_STAFF_REQUIREMENT_DATA,
  payload: data,
});

export const assignShift = (employeeIds, shiftIds, shiftNames) => ({
  type: ASSIGN_SHIFT_REQUEST,
  payload: { employeeIds, shiftIds, shiftNames },
});

export const afterSaveAssignShift = () => ({
  type: AFTER_SAVE_ASSIGN_SHIFT,
});

export const getEmployeesWithShifts = () => ({
  type: GET_EMP_WITH_SHIFT_REQUEST,
});

export const getEmployeesWithWeekoffs = () => ({
  type: GET_EMP_WITH_WEEKOFF_REQUEST,
});

export const saveEmployeeWeekoff = (employeeId, employeeName, shiftIds, day, occasionalWeekoffDays) => ({
  type: SAVE_EMPLOYEE_WEEKOFF_REQUEST,
  payload: { employeeId, employeeName, shiftIds, day, occasionalWeekoffDays },
});

export const afterSaveEmployeeWeekoff = () => ({
  type: AFTER_SAVE_EMPLOYEE_WEEKOFF,
});

