import Header from "../../Layouts/Header";
import Dropdown from 'react-bootstrap/Dropdown';
import {
  Col,
  Container,
  Row,
  Card,
  CardHeader,
  CardBody,
  Offcanvas,
  Modal, ModalBody, ModalHeader
} from "reactstrap";
import triwarning from "../../assets/images/svg/tri-warning.svg"
import greencircle from "../../assets/images/svg/green-circle.svg"
import Skeleton from 'react-loading-skeleton'
import 'react-loading-skeleton/dist/skeleton.css';
import WeekoffPopup from './WeekoffPopup';
import ConfirmationModalNew from "../../Components/Common/ConfirmationModalNew";
import MyCustomTable from "../../Components/Common/MyCustomTable";

import {
  getShiftsNew as onGetShifts,
  getEmployeesWithWeekoffs,
  saveEmployeeWeekoff,
  afterSaveEmployeeWeekoff,
} from "../../store/actions";

import {
  DefaultColumnFilter,
  DateRangeColumnFilter,
  SelectColumnFilter
} from "../../Components/Common/filters";
import { userHasRights, isSuperAdmin, moduleName } from "../../helpers/rights_helper"
import EmployeeTag from "../../Components/Common/EmployeeTag";


const WeekoffList = () => {
  // const [isAllChecked, setIsAllChecked] = useState(false);
  // const handleSelectAll = useCallback((isChecked) => {
  //   setIsAllChecked(isChecked);
  // }, []);
  const dispatch = useDispatch();

  const { employees, isEmployeesSuccess, isEmployeeWeekOffFetching, error, noEmployeeYet, shifts, noShiftYet, loadingForSaveEmployeeWeekoff } = useSelector((state) => ({
    employees: state.Employeesshifts.employeesWithWeekoffs,
    isEmployeesSuccess: state.Employeesshifts.isEmployeesSuccess,
    isEmployeeWeekOffFetching: state.Employeesshifts.isEmployeeWeekOffFetching,
    error: state.Employeesshifts.error,
    noEmployeeYet: state.Employeesshifts.noEmployeeWithWeekoffYet,
    shifts: state.Shift.shifts,
    noShiftYet: state.Shift.noShiftYet,
    loadingForAssignShift: state.Employeesshifts.loadingForAssignShift,
    isSaveAssignShift: state.Employeesshifts.isSaveAssignShift,
  }));

  // useEffect(() => {
  //   if (shifts && !shifts.length && !noShiftYet) {
  //     dispatch(onGetShifts());
  //   }
  // }, [dispatch, shifts, noShiftYet]);


  useEffect(() => {
    if ((!employees || !employees.length) && !noEmployeeYet) {
      dispatch(getEmployeesWithWeekoffs());
    } else if (+sessionStorage.getItem('isResetEmployeeWithWeekoffList') === 1) {
      dispatch(getEmployeesWithWeekoffs());
      sessionStorage.setItem('isResetEmployeeWithWeekoffList', 0)
    }
  }, [dispatch, employees, noEmployeeYet]);

  // const [isModalOpen, setIsModalOpen] = useState(false); 
  // const [dropdownHide, setdropdownHide] = useState(false);
  // const [weekoffData, setWeekoffData] = useState(null);
  // const [alternateDayName, setAlternateDayName] = useState(null);

  // const toggleModal = useCallback(() => {
  //   setIsModalOpen(!isModalOpen)
  // }, [isModalOpen])

  // const getShiftDay = useCallback((shiftId, day) => {
  //   const getShift = shifts.find(shift => parseInt(shift.id) === parseInt(shiftId));
  //   if (!getShift) return false;
  //   const shiftDays = getShift.shift_daywise_data !== undefined ? JSON.parse(getShift.shift_daywise_data) : [];
  //   const getDay = shiftDays.find(shiftDay => parseInt(shiftDay.day) === parseInt(day));
  //   return getDay;
  // }, [shifts])

  // const isDefaultWeekoff = (shiftDayData) => {  
  //   if (!shiftDayData) return false;
  //   const occasionalDays = shiftDayData.occasional_working_days !== undefined && shiftDayData.occasional_working_days !== null ? shiftDayData.occasional_working_days : [];
  //   return (parseInt(shiftDayData.is_weekoff) === 1 && occasionalDays.length === 0);
  // };

  // const isOccasionalWorkingDay = (occasionalDays) => {  
  //   if (!occasionalDays) return false;
  //   return occasionalDays.length > 0 ? true : false;
  // };

  // const isEmployeeWeekoff = useCallback((day, employeeId, shiftId) => {
  //   const getEmployee = employees.find(employee => parseInt(employee.id) === parseInt(employeeId));
  //   if (!getEmployee) return false;
  //   const shiftDays = getEmployee.shift_daywise_data !== undefined ? JSON.parse(getEmployee.shift_daywise_data) : [];
  //   const getDay = shiftDays.find(shiftDay => (parseInt(shiftDay.day) === parseInt(day) && parseInt(shiftDay.shift_id) === parseInt(shiftId)));
  //   if (!getDay) return false;
  //   return parseInt(getDay.is_weekoff) === 1;
  // }, [employees])

  // const getShiftName = useCallback((shiftId, defaultReturn = "-") => {
  //   const targetShift = shifts.find(shift => parseInt(shift.id) === parseInt(shiftId));
  //   return targetShift ? targetShift.name : defaultReturn;
  // }, [shifts])

  // const handleSaveWeekOff = useCallback((employeeId, employeeName, shiftId, day, isEmployeeOff) => {
  //   const shiftName = getShiftName(shiftId);
  //   const method = isEmployeeOff ? 'delete' : 'add';
  //   dispatch(saveEmployeeWeekoff(employeeId, employeeName, shiftId, shiftName, day, method));
  //   dispatch(afterSaveEmployeeWeekoff());
  // }, [dispatch, getShiftName])

  // const handleAlternateConfirm = () => {
  //   const employeeId = weekoffData.employeeId
  //   const employeeName = weekoffData.employeeName
  //   const shiftId = weekoffData.shiftId
  //   const day = weekoffData.day
  //   const shiftName = getShiftName(weekoffData.shiftId);
  //   const method = weekoffData.isEmployeeOff ? 'delete' : 'add'; 
  //   dispatch(saveEmployeeWeekoff(employeeId, employeeName, shiftId, shiftName, day, method));
  //   dispatch(afterSaveEmployeeWeekoff());
  //   toggleModal();
  // }

  const [openDropdownId, setOpenDropdownId] = useState(null);
  const [selectedTagsDropdownValue, setSelectedTagsDropdownValue] = useState(null);

  const handleDropdownClick = useCallback((popupId) => {
    if (openDropdownId === popupId) {
      // If clicking the same dropdown that's already open, close it
      setOpenDropdownId(null);
    } else {
      // If clicking a different dropdown or opening for the first time, open it
      setOpenDropdownId(popupId);
    }
  }, [openDropdownId]);

  const closeDropDown = useCallback(() => {
    setOpenDropdownId(null);
  }, []);

  const handleTagsDropdownChange = useCallback((e) => {
    setSelectedTagsDropdownValue(e.value);
