import React, { useEffect, useState, useCallback, useMemo } from "react";
import Header from "../../Layouts/Header";
import Dropdown from 'react-bootstrap/Dropdown';
import {
  Col,
  Container,
  Row,
  Card,
  CardHeader,
  CardBody,
  Offcanvas,
  Modal, ModalBody, ModalHeader
} from "reactstrap";
import triwarning from "../../assets/images/svg/tri-warning.svg"
import greencircle from "../../assets/images/svg/green-circle.svg"
import Skeleton from 'react-loading-skeleton'
import 'react-loading-skeleton/dist/skeleton.css';
import WeekoffPopup from './WeekoffPopup';
import ConfirmationModalNew from "../../Components/Common/ConfirmationModalNew";
import MyCustomTable from "../../Components/Common/MyCustomTable";
import { useEmployeeWeekoffs } from "../../hooks/useWeekoffQueries";

import {
  DefaultColumnFilter,
  DateRangeColumnFilter,
  SelectColumnFilter
} from "../../Components/Common/filters";
import { userHasRights, isSuperAdmin, moduleName } from "../../helpers/rights_helper"
import EmployeeTag from "../../Components/Common/EmployeeTag";


const WeekoffList = () => {
  // Employee selection state
  const [selectedEmployees, setSelectedEmployees] = useState(new Set());
