import React, { useCallback, useEffect, useState } from 'react'
import Dropdown from 'react-bootstrap/Dropdown';
import { Dropdown as Library1Dropdown } from "primereact/dropdown";
import { userHasRights, isSuperAdmin, moduleName } from "../../helpers/rights_helper"
import Checkbox from 'antd/es/checkbox/Checkbox';
import { useSaveWeekoff, useBulkSaveWeekoff } from '../../hooks/useWeekoffQueries';
import { Spinner } from 'reactstrap';

const WeekoffPopup = ({ closeDropDown, dayName, occasionalWeekoffDays = [], weekoffData, dropdownOptions, selectedDropdownValue, onDropdownChange }) => {
  // Detect if this is bulk mode (multiple employees)
  const isBulkMode = Array.isArray(weekoffData.employeeIds);

  // React Query hooks
  const saveWeekoffMutation = useSaveWeekoff();
  const bulkSaveWeekoffMutation = useBulkSaveWeekoff();

  const [oldWeekoffDays, setOldWeekoffDays] = useState(occasionalWeekoffDays);
  const [selectedWeekoffDays, setSelectedWeekoffDays] = useState(occasionalWeekoffDays)

  // Auto-select all days by default for both individual and bulk mode
  useEffect(() => {
    const defaultDays = isBulkMode || occasionalWeekoffDays.length === 0 ? [1,2,3,4,5] : occasionalWeekoffDays;
    setSelectedWeekoffDays(defaultDays);
    setOldWeekoffDays(occasionalWeekoffDays);
  }, [occasionalWeekoffDays, isBulkMode]);

  // Close popup after successful save
  useEffect(() => {
    if (saveWeekoffMutation.isSuccess || bulkSaveWeekoffMutation.isSuccess) {
      closeDropDown();
      saveWeekoffMutation.reset();
      bulkSaveWeekoffMutation.reset();
    }
  }, [saveWeekoffMutation.isSuccess, bulkSaveWeekoffMutation.isSuccess, closeDropDown, saveWeekoffMutation, bulkSaveWeekoffMutation]);

  const handleSelectWeekOff = useCallback((e, day) => {
    e.stopPropagation();
    if (e.target.checked) {
      setSelectedWeekoffDays(prev => ([...prev, day]));
    } else {
      setSelectedWeekoffDays(prev => prev.filter(p => p !== day));
    }
  }, []);

  const handleSelectAll = useCallback((e) => {
    e.stopPropagation();
    if (e.target.checked) {
      setSelectedWeekoffDays([1, 2, 3, 4, 5]);
    } else {
      setSelectedWeekoffDays([]);
    }
  }, [])

  // Handle save weekoff (individual or bulk)
  const handleSaveWeekOff = useCallback(() => {
    if (isBulkMode) {
      // Bulk save for multiple employees
      bulkSaveWeekoffMutation.mutate({
        employeeIds: weekoffData.employeeIds,
        day: selectedDropdownValue?.value,
        occasionalWeekoffDays: selectedWeekoffDays
      });
    } else {
      // Individual save
      const obj = JSON.parse(sessionStorage.getItem("authUser"));
      saveWeekoffMutation.mutate({
        employee_id: weekoffData.employeeId,
        employee_name: weekoffData.employeeName,
        shift_ids: weekoffData.shiftIds,
        day: weekoffData.day,
        occasional_weekoff_days: selectedWeekoffDays,
        username: obj.data.username,
      });
    }
  }, [isBulkMode, bulkSaveWeekoffMutation, saveWeekoffMutation, weekoffData, selectedDropdownValue, selectedWeekoffDays]);


  return (
    <>
      <Dropdown.Menu onClick={(e) => e.stopPropagation()}>
        <div className="card-header ps-rounded-top-rl p-3">
          {!dropdownOptions && (
            <p className="paragraph-md-3">Select Week Off ({dayName})</p>
          )}
          {dropdownOptions && (
            <Library1Dropdown
              value={selectedDropdownValue}
              options={dropdownOptions}
              onChange={onDropdownChange}
              optionLabel="label"
              optionGroupLabel="label"
              optionGroupChildren="items"
              placeholder="Choose Day"
              className="w-100 ps-check-drop"
            />
          )}
        </div>
        <div className="card-body p-3">
          <div className="weekoff-marktable-box ps-border">

            <div className="marktable-box paragraph-lg-2 ps-border-right ps-border-bottom">1<sup>st</sup></div>
            <div className="marktable-box paragraph-lg-2 ps-border-right ps-border-bottom">2<sup>nd</sup></div>
            <div className="marktable-box paragraph-lg-2 ps-border-right ps-border-bottom">3<sup>rd</sup></div>
            <div className="marktable-box paragraph-lg-2 ps-border-right ps-border-bottom">4<sup>th</sup></div>
            <div className="marktable-box paragraph-lg-2 ps-border-bottom">5<sup>th</sup></div>

            {
              [1, 2, 3, 4, 5].map(day => {
                const dayClass = day !== 5 ? 'ps-border-right' : '';
                return (
                  <div key={day} className={`marktable-box ${dayClass}`}>
                    <Checkbox key={day} checked={selectedWeekoffDays.includes(day)} onClick={(e) => handleSelectWeekOff(e, day)} />
                  </div>
                )
              })
            }
          </div>
          {/* <p>
            <Checkbox
              type='checkbox'
              id={`mark-all-${dayName}-${weekoffData.employeeId}`}
              onClick={handleSelectAll}
              checked={selectedWeekoffDays.length === 5}
            />
            <label htmlFor={`mark-all-${dayName}-${weekoffData.employeeId}`} className='ms-2 cursor-pointer' >
              Mark all {dayName} as week off
            </label>
          </p> */}


          {/* {userHasRights(moduleName.employeeWekoff, 'C') && 
                <button type="button" className="btn btn-blue w-100" data-bs-toggle="modal" data-bs-target="#myModal" onClick={() => {
                  setAlternateDayName(dayName)
                  setWeekoffData(weekoffData)
                  toggleModal();
                }}>Mark WO For Every {dayName}</button>
              } */}
        </div>
        <div className='modal-footer border-top p-3'>
          <button
            type="button"
            className="ps-btn sm-btn grey-outline-btn"
            data-bs-dismiss="modal"
            onClick={closeDropDown}
          >
            Cancel
          </button>

          <button
            type="button"
            className="ps-btn sm-btn primary-btn ms-2"
            id="delete-record"
            onClick={handleSaveWeekOff}
            disabled={
              (isBulkMode 
                ? (bulkSaveWeekoffMutation.isPending || weekoffData.employeeIds?.length === 0 || !selectedDropdownValue)
                : (saveWeekoffMutation.isPending || (JSON.stringify(oldWeekoffDays.sort((a, b) => a - b)) === JSON.stringify(selectedWeekoffDays.sort((a, b) => a - b))))
              )
            }
          >
            {(isBulkMode ? bulkSaveWeekoffMutation.isPending : saveWeekoffMutation.isPending) 
              ? <><Spinner size="sm" className='me-1' />Saving...</> 
              : "Apply Changes"
            }
          </button>
        </div>
      </Dropdown.Menu>
    </>
  )
}

export default WeekoffPopup;

