      INSERT INTO employee_weekoffs
        (ho_id, employee_id, day, occasional_weekoff_days, status, modified, created)
      VALUES ?
      ON DUPLICATE KEY UPDATE
        occasional_weekoff_days=VALUES(occasional_weekoff_days),
        status=VALUES(status),
        modified=VALUES(modified)
