const mysql = require('mysql');
const SecretsManager = require('./secretsManager.js');
const jwt = require('jsonwebtoken');
const crypto = require('crypto');
const jwtTokenLayer = require('/opt/nodejs/app.js');
const remoteNotificationLayer = require('/opt/nodejs/firebase_notification_send.js');
const publishLogLayer = require('/opt/nodejs/publish_activity_log.js');

var pool;
const AWS = require('aws-sdk');
AWS.config.update({
  region: 'ap-south-1',
  maxRetries: 2,
  httpOptions: {
    timeout: 3000,
    connectTimeout: 3000
  }
});
var sqs = new AWS.SQS({ apiVersion: 'latest' });

async function getConnection() {
  return new Promise((resolve, reject) => {
    pool.getConnection(function (err, connection) {
      if (err) {
        //console.log('Error while getting connection');
        reject();
      }
      resolve(connection);
    });
  });
}

async function transactionRelease(connection){
  return new Promise((resolve, reject) => {
      connection.destroy();

      if (connection.state === 'disconnected') {
          console.log('Connection destroyed successfully.');
      } else {
          console.log('Connection released back to pool successfully.');
      }
      resolve();
  });
}

function execQuery(query, connection) {
  return new Promise((resolve, reject) => {
    connection.query(query, function (err, result, fields) {
      if (err) {
        console.log("Error IN query : ", query);
        reject(err);
      } else {
        resolve(result);
      }
    });
  });
}


function send_error(msg) {
  return new Promise(async (resolve, reject) => {
    console.log(msg);
    resolve();
  });
}


exports.handler = async (event, context) => {
  //use secret manager

  var secretName = process.env.payroll_secret_name;
  var region = process.env.payroll_secret_region;
  var secret_manager_value = await SecretsManager.getSecret(secretName, region);

  var secret_manager_result;
  if (typeof secret_manager_value === 'string') {
    secret_manager_result = JSON.parse(secret_manager_value);
  } else {
    secret_manager_result = secret_manager_value;
  }

  if (typeof pool === 'undefined') {
    var payroll_config = {
      database: secret_manager_result.dbname,
      user: secret_manager_result.username,
      password: secret_manager_result.password,
      host: secret_manager_result.host
    };
    pool = mysql.createPool(payroll_config);
  }

  //use secret manager


  var db = await getConnection();
  let api_response = {};
  let response_status;
  let response_message;
  let response_data = {};
  let validation_errors = {};
  let response_code;

  try {
    let user_ip = (event.sourceIP !== undefined) ? event.sourceIP : '';
    let user_agent = (event.headers['User-Agent'] !== undefined) ? event.headers['User-Agent'] : '';
    const request_details = {'ip': user_ip, 'user_agent': user_agent}

    let request_headers = JSON.parse(JSON.stringify(event.headers));
    let request_body = JSON.parse(JSON.stringify(event.body));

    console.log(request_body)
    let validate_request = await jwtTokenLayer.validate_user_request(request_headers, request_body);

    if (validate_request.status === 1) {

      if (request_headers.method !== undefined && request_headers.method !== '') {

        request_body.org_id = validate_request.user_data.org_id;
        request_body.ho_id = validate_request.user_data.ho_id;
        request_body.user_id = validate_request.user_data.user_id;
        if(request_body.username === undefined || request_body.username === null) {
          request_body.username = await getUserName(request_body.user_id,db);
        }
        request_body.organization_id = validate_request.user_data.org_id;
        request_body.shift_module_mode = parseInt(validate_request.user_data.shift_module_mode) || 1;
        request_body.face_app = validate_request.user_data.face_app ?? '';
        request_body.department_ids = validate_request.user_data.department_ids ?? null;
        request_body.face_app_device_id = validate_request.user_data.face_app_device_id ?? '';
        request_body.apk_version = validate_request.user_data.apk_version ?? 0;
        request_body.rights = validate_request.user_data.rights ?? '';

        if (request_headers.method === 'save_shift_new') {

          if (request_body.shift_daywise_data && Object.keys(request_body.shift_daywise_data).length > 0) {

            let validate_request_data = await validate_shift_new(request_body, request_body.id, db);
            if (!validate_request_data.success) {
              response_status = 0;
              response_message = 'Invalid Request';
              validation_errors = validate_request_data.errors;
              response_code = 200;
            } else {
              let shift_id;
              if (request_body.id !== undefined && request_body.id > 0) {
                request_body.shift_id = request_body.id;
                const oldShift = await getShiftOldData(request_body, db);
                let save_shift = await update_shift_new(request_body, user_agent, user_ip, db);
                shift_id = request_body.id;
                await deleteJEShiftExists( request_body.ho_id, request_body.id, request_body.shift_module_mode, db );
                 //* SNS call
                request_body.sub_module_id = request_body.id;
                request_body.old_value = oldShift;
                await insertLogForShiftShiftAddOrEdit(
                  request_body,
                  request_details,
                  2
                );
              } else {
                let save_shift = await add_shift_new(request_body, db);

                //* SNS call
                shift_id = save_shift.insertId;
                request_body.sub_module_id = save_shift.insertId
                await insertLogForShiftShiftAddOrEdit(
                  request_body,
                  request_details,
                  1
                );
              }

              if (shift_id > 0) {

                let shiftDaywiseData = request_body.shift_daywise_data.map(async dayData => {

                  const {
                      day,
                      from_time,
                      to_time,
                      is_weekoff,
                      occasional_working_days
                  } = dayData;

                  const converted_from_time = from_time ? await tConvert(from_time) : null;
                  const converted_to_time = to_time ? await tConvert(to_time) : null;

                  return {
                      day,
                      from_time: is_weekoff && occasional_working_days.length === 0 ? null : converted_from_time,
                      to_time: is_weekoff && occasional_working_days.length === 0 ? null : converted_to_time,
                      is_weekoff,
                      occasional_working_days
                  };
              });

              shiftDaywiseData = await Promise.all(shiftDaywiseData);

              let newShiftData = {
                  'id': shift_id,
                  'name': request_body.shift_name,
                  'description': request_body.shift_description,
                  'added_on' : customFormatDate(new Date())['date'],
                  'shift_daywise_data': JSON.stringify(shiftDaywiseData),
                  ...(request_body.id === undefined && { employee_count: 0 })
              };

                response_status = 1;
                response_message = 'Shift data added/updated successfully';
                response_data = newShiftData;
                response_code = 200;

              } else {
                response_status = 0;
                response_message = 'Something went wrong for saving shift';
                response_code = 500;
              }
            }
          } else {
            response_status = 0;
            response_message = 'Please provide the valid data';
            response_code = 400;
          }
        } else if (request_headers.method === 'delete_shift_new') {

          if (request_body.shift_id !== undefined && request_body.shift_id > 0) {
            let shiftDelete = await delete_shift_new(request_body.shift_id, db);

            if (shiftDelete.affectedRows > 0) {
              //* SNS call
               const oldShift = await getShiftOldData(request_body, db);
               request_body.sub_module_id = request_body.shift_id;
               request_body.old_value = oldShift;
               request_body.shift_name = oldShift[0]?.name
               await insertLogForShiftShiftAddOrEdit(
                 request_body,
                 request_details,
                 3
               );
              response_status = 1;
              response_message = 'shift deleted successfully';
              response_code = 200;
