                await execQuery(updateModeQuery, db);

                response_status = 1;
                response_message = 'Successfully switched to Advanced Shift mode';
                response_code = 200;
              }

            } catch (error) {
              console.error('Error switching shift module mode:', error);
              response_status = 0;
              response_message = 'Failed to switch shift mode';
              response_code = 500;
            }

          } else {
            response_status = 0;
            response_message = 'Invalid shift_module_mode. Must be 1 or 2';
            response_code = 400;
          }
        } 
      } else {
        response_status = 0;
        response_message = 'Bad Request';
        response_code = 400;
      }
    } else {
      response_status = validate_request.status;
      response_message = validate_request.msg;
      response_code = 400;
    }
  } catch (e) {
    let error_obj = { "errorlog": e.stack, "request_data": event };
    await send_error(error_obj);
    response_status = 0;
    response_message = 'Internal Server Error';
    response_code = 500;

  } finally {
    console.log('finally block called...');
    await transactionRelease(db);
  }

  api_response = { 'status': response_status, 'message': response_message, 'statusCode': response_code };

  if (Object.keys(response_data).length > 0) {
    api_response.data = response_data;
  }

  if (Object.keys(validation_errors).length > 0) {
    api_response.validation_errors = validation_errors;
  }

  return api_response;
}

async function validate_shift_new(req_body, shift_id, db) {

  try {
    let data_validated = true;
    let errors = {};
    let indexId = 0;

    const values = [
      req_body.ho_id,
      req_body.shift_name.replace(/ /g, "").toLowerCase(),
      1
    ];

    let check_shift_name = "SELECT COUNT(*) AS cnt FROM shifts WHERE ho_id = ? AND LOWER(REPLACE(name, ' ', '')) = ? AND status = ?";
    if (shift_id !== undefined && shift_id > 0) {
        check_shift_name += " AND id != ?";
        values.push(shift_id);
        indexId = shift_id;
    }

    const formattedQuery = mysql.format(check_shift_name, values);
    let res_shift_name = await execQuery(formattedQuery, db);

    if (res_shift_name[0].cnt > 0) {
      errors[`shift_name_${indexId}`] = 'Shift name already exists';
      data_validated = false;
    }else if(req_body.shift_name.includes('¶')){
      errors[`shift_name_${indexId}`] = 'Special characters are not allowed';
      data_validated = false;
    }

    const shiftDaywiseData = req_body.shift_daywise_data;
    for (const dayData of shiftDaywiseData) {
      const { day, from_time, to_time } = dayData;
      if (from_time && to_time) {
        let checkShiftDetailsQuery = `
          SELECT COUNT(*) AS cnt
          FROM shift_details
          WHERE ho_id = ? AND day = ? AND start_time = ? AND end_time = ? AND status = 1
        `;

        const valuesSDQ = [req_body.ho_id, day, from_time, to_time];

        if (shift_id !== undefined && shift_id > 0) {
          checkShiftDetailsQuery += " AND shift_id != ?";
          valuesSDQ.push(shift_id);
        }

        const formattedCheckShiftDetailsQuery = mysql.format(checkShiftDetailsQuery, valuesSDQ);

        console.log( 'checkShiftDetailsQuery >>>>', checkShiftDetailsQuery)

        const resShiftDetails = await execQuery(formattedCheckShiftDetailsQuery, db);

        if (resShiftDetails.length > 0 && resShiftDetails[0].cnt > 0) {
          const adjustedDay = day - 1;
          const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
          const dayOfWeek = daysOfWeek[adjustedDay];

          errors[`shift_from_time_${indexId}_${dayOfWeek}`] = `Shift with the same time already exists`;
          data_validated = false;
          break;
        }
      }
    }

    if (shift_id > 0) {
      const assignedShiftQuery = mysql.format(
        `
        SELECT DISTINCT esa.shift_id, sd.day, sd.start_time, sd.end_time
        FROM employee_shift_assignments esa
        JOIN shift_details sd ON esa.shift_id = sd.shift_id AND esa.day = sd.day AND sd.status = 1
        JOIN employee_shift_assignments esa_filter ON esa.employee_id = esa_filter.employee_id AND esa_filter.day = esa.day AND esa_filter.status = 1
        WHERE esa_filter.shift_id = ?
          AND esa.shift_id != ?
          AND esa.status = 1;
        `,
        [shift_id, shift_id]
      );

      console.log("assignedShiftQuery >>> ",assignedShiftQuery);

      const assignedShift = await execQuery(assignedShiftQuery, db);

      for (let i = 0; i < assignedShift.length; i++) {
        const shift = assignedShift[i];
        const newTime = shiftDaywiseData[shift.day - 1];

        if (newTime.from_time < shift.end_time && shift.start_time < newTime.to_time) {
          data_validated = false;
          errors["shift_common_error"] = 'Assigned shifts timings are overlapping with new timings';
          break;
        }
      }
    }

    return { 'success': data_validated, 'errors': errors};
  } catch (err) {
    throw err;
  }

}

