async function get_shifts_data_new(request_body, db) {

  let qry = '';
  qry = `
    SELECT 
      s.id,
      s.name,
      s.description,
      s.default_shift,
      s.ranking,
      DATE_FORMAT(s.created, '%Y-%m-%d') as added_on,
      JSON_ARRAYAGG(
            JSON_OBJECT(
              'day', sd.day,
              'from_time', IFNULL(DATE_FORMAT(sd.start_time, "%h:%i %p"), null),
              'to_time', IFNULL(DATE_FORMAT(sd.end_time, "%h:%i %p"), null)
            )
          ) AS shift_daywise_data,
          (
            SELECT COUNT(DISTINCT CASE WHEN esn.status = 1 THEN esn.employee_id ELSE NULL END)
          FROM employee_shift_assignments esn
          WHERE esn.ho_id = ${request_body.ho_id} AND esn.shift_id = s.id
        ) AS employee_count    
        FROM 
        shifts s
        LEFT JOIN 
        shift_details sd ON sd.ho_id = ${request_body.ho_id} and s.id = sd.shift_id
        WHERE 
        s.ho_id = ${request_body.ho_id}
        AND s.status = 1
        GROUP BY 
        s.id
        ORDER BY 
        s.created DESC
    `;
  //let qry = `SELECT id,name,description,default_shift,ranking,DATE_FORMAT(from_time, "%h:%i %p") as shift_from_time,DATE_FORMAT(to_time, "%h:%i %p") as shift_to_time FROM shifts WHERE organization_id = ${request_body.org_id} AND status = 1 order by ranking ASC`;

  let org_shift_data = await execQuery(qry, db);

  return org_shift_data;
}

async function tConvert(time) {
  // Check correct time format and split into components
  time = time.toString().match(/^([01]\d|2[0-3])(:)([0-5]\d)(:[0-5]\d)?$/) || [time];

  if (time.length > 1) { // If time format correct
    time = time.slice(1);  // Remove full string match value
    time[5] = +time[0] < 12 ? ' AM' : ' PM'; // Set AM/PM
    //time[0] = +time[0] % 12 || 12; // Adjust hours
    time[0] = ('0' + (+time[0] % 12 || 12)).slice(-2);

  }
  return time.join(''); // return adjusted time or original string
}


