/**
 * Creates new shift assignments for all 7 days of the week
 * @param {Object} requestedData - Contains employee and shift details
 * @param {string} currentdatetime - Current timestamp
 * @param {Object} db - Database connection
 */
async function insertNewAssignments(requestedData, currentdatetime, db) {
  const employeeIds = requestedData.employeeIds;
  const shiftIds = requestedData.shiftIds;
  const values = [];

  // Get today/tomorrow to set immediate sync priority
  const { today: todayDay, tomorrow: tomorrowDay } = getTodayTomorrowDayNumbersUTC();

  // Create shift assignment for every day (Sunday=1 to Saturday=7)
  employeeIds.forEach(employee => {
    shiftIds.forEach(shiftId => {
      for(let day = 1; day <= 7; day++) {
        // check for Today/tomorrow changes
        const isJeSynced = (day === todayDay || day === tomorrowDay) ? 0 : 1;

        values.push([
          requestedData.ho_id,
          employee.id,
          day,
          shiftId,
          employee.department_id,
          employee.department_name,
          employee.designation_id,
          employee.designation_name,
          isJeSynced, // is_je_synced: 0 for today/tomorrow, 1 for others
          4, // is_notified
          1, // status
          currentdatetime,
          currentdatetime
        ]);
      }
    });
  });

  if (values.length > 0) {
    const insertQuery = `
      INSERT INTO employee_shift_assignments 
        (ho_id, employee_id, day, shift_id, department_id, department_name, designation_id, designation_name, is_je_synced, is_notified, status, created, modified) 
      VALUES ?
      ON DUPLICATE KEY UPDATE
        department_id = VALUES(department_id), 
        department_name = VALUES(department_name),
        designation_id = VALUES(designation_id),
        designation_name = VALUES(designation_name),
        is_je_synced = VALUES(is_je_synced),
        is_notified = VALUES(is_notified),
        status = 1, 
        modified = VALUES(modified)
    `;

    const formattedQuery = mysql.format(insertQuery, [values]);
    console.log('insertNewAssignments query >>', formattedQuery);

    await execQuery(formattedQuery, db);
  }
}

/**
 * Creates audit logs to track who assigned shifts to which employees
 * @param {Object} requestedData - Contains employee and shift details
 * @param {string} user_agent - User browser information
 * @param {string} user_ip - User IP address
 * @param {string} currentdatetime - Current timestamp
 * @param {Object} db - Database connection
 */
async function createAuditLogs(requestedData, user_agent, user_ip, currentdatetime, db) {
  const employeeIds = requestedData.employeeIds;
  const shiftIds = requestedData.shiftIds;
  const log_values = [];

  employeeIds.forEach(employee => {
    // Get old shift information for comparison
    const oldShiftIds = employee.shift_ids 
      ? employee.shift_ids.split(',').map(oldShiftId => parseInt(oldShiftId)).filter(Number.isFinite) 
      : [];
    const newShiftIds = shiftIds.map(newShiftId => parseInt(newShiftId));

    // Get shift names for logging
    const oldShiftNames = employee.oldShiftNames ?? [];
    const newShiftNames = requestedData.shiftNames;
    const unassignedShifts = oldShiftNames.filter(oldShiftName => !newShiftNames.includes(oldShiftName));

    // Log unassigned shifts if any
    if(unassignedShifts.length > 0) {
      const unassignedShiftLog = generate_shift_log(
        employee.id, 
        employee.name, 
        unassignedShifts,
        requestedData.shiftNames, 
        oldShiftIds, 
        newShiftIds, 
        2, // operation type for unassign
        requestedData, 
        user_agent, 
        user_ip, 
        currentdatetime
      );
      log_values.push(unassignedShiftLog);
    }

    // Log assigned shifts
    const assignedShiftLog = generate_shift_log(
      employee.id, 
      employee.name, 
      employee.oldShiftNames, 
      requestedData.shiftNames, 
      oldShiftIds, 
      newShiftIds, 
      1, // operation type for assign
      requestedData, 
      user_agent, 
      user_ip, 
      currentdatetime
    );
    log_values.push(assignedShiftLog);
  });

  // Save all logs
  if (log_values.length > 0) {
    await save_shift_log(log_values, db);
  }
}

/**
 * Gets today and tomorrow day numbers where Sunday=1, Saturday=7
 * @returns {Object} Object with today and tomorrow day numbers
 */
function getTodayTomorrowDayNumbersUTC() {
  const now = new Date();
  const mapJSDayToPayloadDay = (jsDay) => jsDay + 1;
  const todayPayloadDayNumber = mapJSDayToPayloadDay(now.getUTCDay());
  const tomorrowDate = new Date(now);
  tomorrowDate.setUTCDate(now.getUTCDate() + 1);
  const tomorrowPayloadDayNumber = mapJSDayToPayloadDay(
    tomorrowDate.getUTCDay()
  );
  return { today: todayPayloadDayNumber, tomorrow: tomorrowPayloadDayNumber };
}

function formatTime(time) {
  if(time === null) {
    return null;
  }
  const [hours, minutes] = time.split(':');
  const period = hours >= 12 ? 'PM' : 'AM';
  const formattedHours = hours % 12 || 12;
  return `${formattedHours}:${minutes} ${period}`;
}

function generate_shift_modified_change_log(shift_id, oldShiftData = [], newShiftData = [], operation, sub_operation, request_body, user_ip, user_agent, currentdatetime) {

  const getDayName = {
    1 : 'Sunday',
    2 : 'Monday',
    3 : 'Tuesday',
    4 : 'Wednesday',
    5 : 'Thursday',
    6 : 'Friday',
    7 : 'Saturday',
  }

  let text_value;

  if (sub_operation === 'time_log') {

    const timeChangeLogs = oldShiftData.map((oldData, index) => {
      const newData = newShiftData[index];
      const dayName = getDayName[oldData.day];

      let changeLog = '';
      const fromTimeChanged = formatTime(oldData.start_time) !== formatTime(newData.from_time);
      const toTimeChanged = formatTime(oldData.end_time) !== formatTime(newData.to_time);

      if (fromTimeChanged && toTimeChanged) {
        if(oldData.start_time === null || oldData.start_time === '') {
          changeLog += `Start Time set to ${formatTime(newData.from_time)},`;
        } else {
          changeLog += `Start Time changed from ${formatTime(oldData.start_time)} to ${formatTime(newData.from_time)},`;
        }
        if(oldData.end_time === null || oldData.end_time === '') {
          changeLog += `End Time set to ${formatTime(newData.to_time)}`;
        } else {
          changeLog += `End Time changed from  ${formatTime(oldData.end_time)} to ${formatTime(newData.to_time)}`;
        }
      } else if (fromTimeChanged) {

        if(oldData.start_time === null || oldData.start_time === '') {
          changeLog += `Start Time set to ${formatTime(newData.from_time)},`;
        } else {
          changeLog += `Start Time changed from ${formatTime(oldData.start_time)} to ${formatTime(newData.from_time)}`;
        }

      } else if (toTimeChanged) {

        if(oldData.end_time === null || oldData.end_time === '') {
          changeLog += `End Time set to ${formatTime(newData.to_time)}`;
        } else {
          changeLog += `End time changed from ${formatTime(oldData.end_time)} to ${formatTime(newData.to_time)}`;
        }

      } else {
        return null; // No change, exclude this day
      }
      return changeLog += ` for ${dayName}`;
    }).filter(log => log !== null).join(';');

    // in case no shift time changes
    if(timeChangeLogs === null || timeChangeLogs === '') {
      return false;
    }
    text_value = timeChangeLogs;
  }

  // week off add log check
  if (sub_operation === 'weekoff_add_log') {
    const weekoffAddLogs = newShiftData.map((newData, index) => {
      const oldData = oldShiftData[index];
      const dayName = getDayName[newData.day];  

      if (!oldData.is_weekoff && newData.is_weekoff) {
        const days = [1,2,3,4,5];
        const weekoffDays = days.filter(day => !newData.occasional_working_days.includes(day));
        const occasionalWorkingDays = weekoffDays.length < 5
          ? `${weekoffDays.join(', ')}`
          : 'all';
        return `${dayName} - ${occasionalWorkingDays}`;
      }
      return null;
    }).filter(log => log !== null).join(';');

    if (weekoffAddLogs === '' || weekoffAddLogs === null) {
      return false;
    }

    text_value = weekoffAddLogs;
  }

  // week off remove log check 
  if (sub_operation === 'weekoff_remove_log') {
    const weekoffRemoveLogs = oldShiftData.map((oldData, index) => {
      const newData = newShiftData[index];
      const dayName = getDayName[oldData.day];

      if (!Array.isArray(oldData.occasional_working_days)) {
        if(oldData.occasional_working_days === null || oldData.occasional_working_days === ''){
          oldData.occasional_working_days = [];
        } else {
          oldData.occasional_working_days = JSON.parse(oldData.occasional_working_days);
        }
      }

      if (oldData.is_weekoff && !newData.is_weekoff) {
        const days = [1,2,3,4,5];
        const weekoffDays = days.filter(day => !oldData.occasional_working_days.includes(day));
        const occasionalWorkingDays = weekoffDays.length < 5
          ? `${weekoffDays.join(', ')}`
          : 'all';
        return `${dayName} - ${occasionalWorkingDays}`;
      }
      return null;
    }).filter(log => log !== null).join(';');

    if (weekoffRemoveLogs === '' || weekoffRemoveLogs === null) {
      return false;
    }

    text_value = weekoffRemoveLogs;
  }

  // week off modified log check
  if (sub_operation === 'weekoff_modified_log') {
    const weekoffModifiedLogs = newShiftData.map((newData, index) => {
      const oldData = oldShiftData[index];
      const dayName = getDayName[newData.day];

      if (!Array.isArray(oldData.occasional_working_days)) {
        if(oldData.occasional_working_days === null || oldData.occasional_working_days === ''){
