              // If switching from Advanced (2) to Simple (1), perform cleanup
              if (targetMode === 1) {

                // Get default shift for this ho_id
                const getDefaultShiftQuery = mysql.format(`
                  SELECT id FROM shifts WHERE ho_id = ? AND default_shift = 1 AND status = 1 LIMIT 1
