                  const deleteWeekoffsQuery = mysql.format(`
                    UPDATE employee_weekoffs SET status = 0, modified = ? WHERE ho_id = ? AND status = 1
                  `, [currentdatetime, request_body.ho_id]);

                  await execQuery(deleteWeekoffsQuery, db);

                  // remove all entries from employees from employee_shift_assignments table
                  const deleteShiftAssignmentsQuery = mysql.format(`
                    UPDATE employee_shift_assignments SET status = 0, modified = ? WHERE ho_id = ? AND status = 1
                  `, [currentdatetime, request_body.ho_id]);

                  await execQuery(deleteShiftAssignmentsQuery, db);

                  // Get all active employees for this ho_id
                  const getActiveEmployeesQuery = mysql.format(`
                    SELECT id FROM employees WHERE ho_id = ? AND status = 1
                  `, [request_body.ho_id]);

                  let activeEmployees = await execQuery(getActiveEmployeesQuery, db);

                  // Assign all active employees to default shift for all 7 days with proper is_je_synced values
                  if (activeEmployees.length > 0) {
                    let assignmentValues = [];

                    activeEmployees.forEach(emp => {
                      // For each employee, create assignments for all 7 days (1=Sunday to 7=Saturday)
                      for (let day = 1; day <= 7; day++) {
                        // Set is_je_synced based on whether it's today or tomorrow
                        const isJeSynced = (day === todayDayNumber || day === tomorrowDayNumber) ? 0 : 1;

                        assignmentValues.push([
                          emp.id,           // employee_id
                          request_body.ho_id, // ho_id
                          day,              // day (1-7)
                          defaultShiftId,   // shift_id
                          0,                // department_id (will be updated later if needed)
                          '',               // department_name 
                          0,                // designation_id (will be updated later if needed)
                          '',               // designation_name
                          isJeSynced,       // is_je_synced
                          4,                // is_notified
                          1,                // status
                          currentdatetime,  // created
                          currentdatetime   // modified
                        ]);
                      }
                    });

                    // Process assignments in batches
                    if (assignmentValues.length > 0) {
                      const BATCH_SIZE = 100; // Process 100 records at a time
                      const totalBatches = Math.ceil(assignmentValues.length / BATCH_SIZE);
                      console.log(`Processing ${assignmentValues.length} shift assignments in ${totalBatches} batches of ${BATCH_SIZE} records each`);
                      const batchPromises = [];

                      for (let i = 0; i < assignmentValues.length; i += BATCH_SIZE) {
                        const batch = assignmentValues.slice(i, i + BATCH_SIZE);
                        const batchIndex = Math.floor(i / BATCH_SIZE) + 1;

                        console.log(`Preparing batch ${batchIndex}/${totalBatches}: records ${i + 1}-${Math.min(i + BATCH_SIZE, assignmentValues.length)}`);

                        // Create formatted query for this batch
                        const assignDefaultShiftQuery = mysql.format(`
                          INSERT INTO employee_shift_assignments 
                          (employee_id, ho_id, day, shift_id, department_id, department_name, designation_id, designation_name, is_je_synced, is_notified, status, created, modified) 
                          VALUES ? ON DUPLICATE KEY UPDATE
                            shift_id = VALUES(shift_id),
                            is_je_synced = VALUES(is_je_synced),
                            is_notified = VALUES(is_notified),
                            status = 1, 
                            modified = VALUES(modified)
                        `, [batch]);

                        batchPromises.push(execQuery(assignDefaultShiftQuery, db));
                      }

                      // Execute all batches concurrently using Promise.all for maximum performance
                      console.log(`Executing all ${totalBatches} batches concurrently...`);
                      const batchResults = await Promise.all(batchPromises);

                      // Calculate total affected rows from all batches
                      const totalAffectedRows = batchResults.reduce((sum, result) => sum + result.affectedRows, 0);
                      console.log(`Successfully processed all ${assignmentValues.length} shift assignments across ${totalBatches} concurrent batches (${totalAffectedRows} total rows affected)`);
                    }
                  }

                  // Update shift_module_mode
                  const updateModeQuery = mysql.format(`
                    INSERT INTO organization_configurations (ho_id, key_name, key_value, status, modified, created)
                    VALUES (?, 'shift_module_mode', ?, 1, ?, ?)
                    ON DUPLICATE KEY UPDATE
                        key_value = VALUES(key_value), modified = VALUES(modified), status = VALUES(status)
                  `, [request_body.ho_id, targetMode, currentdatetime, currentdatetime]);

                  await execQuery(updateModeQuery, db);

                  response_status = 1;
                  response_message = 'Successfully switched to Simple Shift mode and assigned all employees to default shift';
                  response_code = 200;
                }

              } else {
                // Switching from Simple (1) to Advanced (2) - just update mode
                const updateModeQuery = mysql.format(`
                  INSERT INTO organization_configurations (ho_id, key_name, key_value, status, modified, created)
                  VALUES (?, 'shift_module_mode', ?, 1, ?, ?)
                  ON DUPLICATE KEY UPDATE
                      key_value = VALUES(key_value), modified = VALUES(modified), status = VALUES(status)
                `, [request_body.ho_id, targetMode, currentdatetime, currentdatetime]);

                await execQuery(updateModeQuery, db);

                response_status = 1;
                response_message = 'Successfully switched to Advanced Shift mode';
                response_code = 200;
              }

            } catch (error) {
              console.error('Error switching shift module mode:', error);
              response_status = 0;
              response_message = 'Failed to switch shift mode';
              response_code = 500;
            }

          } else {
            response_status = 0;
            response_message = 'Invalid shift_module_mode. Must be 1 or 2';
            response_code = 400;
          }
        } 
      } else {
        response_status = 0;
        response_message = 'Bad Request';
        response_code = 400;
      }
    } else {
      response_status = validate_request.status;
      response_message = validate_request.msg;
      response_code = 400;
    }
  } catch (e) {
    let error_obj = { "errorlog": e.stack, "request_data": event };
    await send_error(error_obj);
    response_status = 0;
    response_message = 'Internal Server Error';
    response_code = 500;

  } finally {
    console.log('finally block called...');
    await transactionRelease(db);
  }

  api_response = { 'status': response_status, 'message': response_message, 'statusCode': response_code };

  if (Object.keys(response_data).length > 0) {
    api_response.data = response_data;
  }

  if (Object.keys(validation_errors).length > 0) {
    api_response.validation_errors = validation_errors;
  }

  return api_response;
}

async function validate_shift_new(req_body, shift_id, db) {

  try {
    let data_validated = true;
    let errors = {};
    let indexId = 0;

    const values = [
      req_body.ho_id,
      req_body.shift_name.replace(/ /g, "").toLowerCase(),
      1
    ];

    let check_shift_name = "SELECT COUNT(*) AS cnt FROM shifts WHERE ho_id = ? AND LOWER(REPLACE(name, ' ', '')) = ? AND status = ?";
    if (shift_id !== undefined && shift_id > 0) {
        check_shift_name += " AND id != ?";
        values.push(shift_id);
        indexId = shift_id;
    }

    const formattedQuery = mysql.format(check_shift_name, values);
    let res_shift_name = await execQuery(formattedQuery, db);

    if (res_shift_name[0].cnt > 0) {
      errors[`shift_name_${indexId}`] = 'Shift name already exists';
      data_validated = false;
    }else if(req_body.shift_name.includes('¶')){
      errors[`shift_name_${indexId}`] = 'Special characters are not allowed';
      data_validated = false;
    }

    const shiftDaywiseData = req_body.shift_daywise_data;
    for (const dayData of shiftDaywiseData) {
      const { day, from_time, to_time } = dayData;
      if (from_time && to_time) {
        let checkShiftDetailsQuery = `
          SELECT COUNT(*) AS cnt
          FROM shift_details
          WHERE ho_id = ? AND day = ? AND start_time = ? AND end_time = ? AND status = 1
        `;

        const valuesSDQ = [req_body.ho_id, day, from_time, to_time];

        if (shift_id !== undefined && shift_id > 0) {
          checkShiftDetailsQuery += " AND shift_id != ?";
          valuesSDQ.push(shift_id);
        }

        const formattedCheckShiftDetailsQuery = mysql.format(checkShiftDetailsQuery, valuesSDQ);

        console.log( 'checkShiftDetailsQuery >>>>', checkShiftDetailsQuery)

        const resShiftDetails = await execQuery(formattedCheckShiftDetailsQuery, db);

        if (resShiftDetails.length > 0 && resShiftDetails[0].cnt > 0) {
          const adjustedDay = day - 1;
          const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
          const dayOfWeek = daysOfWeek[adjustedDay];

          errors[`shift_from_time_${indexId}_${dayOfWeek}`] = `Shift with the same time already exists`;
          data_validated = false;
          break;
        }
      }
    }

    if (shift_id > 0) {
      const assignedShiftQuery = mysql.format(
        `
        SELECT DISTINCT esa.shift_id, sd.day, sd.start_time, sd.end_time
        FROM employee_shift_assignments esa
        JOIN shift_details sd ON esa.shift_id = sd.shift_id AND esa.day = sd.day AND sd.status = 1
        JOIN employee_shift_assignments esa_filter ON esa.employee_id = esa_filter.employee_id AND esa_filter.day = esa.day AND esa_filter.status = 1
        WHERE esa_filter.shift_id = ?
          AND esa.shift_id != ?
          AND esa.status = 1;
        `,
        [shift_id, shift_id]
      );

      console.log("assignedShiftQuery >>> ",assignedShiftQuery);

      const assignedShift = await execQuery(assignedShiftQuery, db);

      for (let i = 0; i < assignedShift.length; i++) {
        const shift = assignedShift[i];
        const newTime = shiftDaywiseData[shift.day - 1];

        if (newTime.from_time < shift.end_time && shift.start_time < newTime.to_time) {
          data_validated = false;
          errors["shift_common_error"] = 'Assigned shifts timings are overlapping with new timings';
          break;
        }
      }
    }

    return { 'success': data_validated, 'errors': errors};
  } catch (err) {
    throw err;
  }

}

