async function assign_employee_shifts_new(requestedData, user_agent, user_ip, db) {
  try {
    // Start transaction
    await execQuery('START TRANSACTION', db);
    const currentdatetime = customFormatDate(new Date())['date_formatted'];

    // Clear old assignments and week-offs for all 7 days
    await deactivateExistingAssignments(requestedData, currentdatetime, db);

    // Insert new assignments for all employees and shifts
    await insertNewAssignments(requestedData, currentdatetime, db);

    // Create audit logs for the changes
    await createAuditLogs(requestedData, user_agent, user_ip, currentdatetime, db);

    // commit the transaction
    await execQuery('COMMIT', db);

  } catch (error) {
    console.error('Error:', error);
    await execQuery('ROLLBACK', db);
    throw error;
  }

}

