      "UPDATE", // operation
      JSON.stringify(request_details), // request_details
      'SHIFT_ASSIGNMENT_LOGS', // action_context
      logTitle, // log_title
      text_value, // log_descriptions
      null, //extra_data
      "Web App", // request_from
      currentdatetime, // created
      currentdatetime, // deleted
    ]
  // return log data
  // return [
  //   request_body.user_id, // user_id
  //   request_body.ho_id, // ho_id
  //   null, // employee_id
  //   shift_id, // shift_id
  //   JSON.stringify([]), // old_values
