}

async function encryptData(data, key) {
  const iv = crypto.randomBytes(16);
  const cipher = crypto.createCipheriv('aes-256-cbc', Buffer.from(key), iv);
  let encryptedData = cipher.update(JSON.stringify(data), 'utf-8', 'hex');
  encryptedData += cipher.final('hex');
  return {
      iv: iv.toString('hex'),
      encryptedData: encryptedData
  };
}

async function checkShiftOverlaps(request_body, db) {

  let shiftIds = request_body.shiftIds;

  console.log('shiftIds >>', shiftIds)

  if(shiftIds.length > 0) {

    const query = `
      WITH shift_times AS (
          SELECT 
              shift_id, 
              start_time, 
              CASE 
                  WHEN end_time < start_time THEN DATE_ADD(end_time, INTERVAL 1 DAY)
                  ELSE end_time
              END AS end_time
          FROM 
              shift_details
          WHERE 
              shift_id IN (?)
              AND status = 1
              AND day BETWEEN 1 AND 7
      ),
      normalized_shifts AS (
          SELECT 
              shift_id,
              TIME_TO_SEC(start_time) AS start_seconds,
              TIME_TO_SEC(end_time) AS end_seconds
          FROM 
              shift_times
      ),
      adjusted_shifts AS (
          SELECT 
              shift_id,
              start_seconds,
              CASE
                  WHEN end_seconds <= start_seconds THEN end_seconds + 86400
                  ELSE end_seconds
              END AS end_seconds
          FROM 
              normalized_shifts
      )
      SELECT 
          st1.shift_id AS shift_id1, 
          st2.shift_id AS shift_id2,
          st1.start_seconds AS start_seconds1, 
          st1.end_seconds AS end_seconds1,
          st2.start_seconds AS start_seconds2, 
          st2.end_seconds AS end_seconds2,
          CASE 
              WHEN (st2.start_seconds BETWEEN st1.start_seconds AND st1.end_seconds) 
                  OR (st2.end_seconds BETWEEN st1.start_seconds AND st1.end_seconds) 
                  OR (st1.start_seconds BETWEEN st2.start_seconds AND st2.end_seconds) 
                  OR (st1.end_seconds BETWEEN st2.start_seconds AND st2.end_seconds)
              THEN 'Overlap'
              ELSE 'No Overlap'
          END AS overlap_status
      FROM 
          adjusted_shifts st1
      JOIN 
          adjusted_shifts st2
      ON 
          st1.shift_id < st2.shift_id;

    `;

    const formattedQuery = mysql.format(query, [shiftIds]);

    console.log('formattedQuery shiftIds >>', formattedQuery)


    let rows = await execQuery(formattedQuery, db);

    const overlap = rows.some(row => row.overlap_status === 'Overlap');

    console.log('overlap shiftIds >>', overlap)


    return overlap;
  } else {
    return false;
  }

}


async function callSQS(data, retry = 0) {
  try {

    let params = {
      MessageBody: JSON.stringify({ data }),
      QueueUrl: process.env.sqs_shift_change_events
    };

    console.log('sqs send >>> ');

    try {
      let sqsResponse = await sqs.sendMessage(params).promise();
      console.log('SQS Response>>> ', sqsResponse);

      console.log('Message sent successfully >>>>> ');
    }
    catch (error) {
      console.error('Error sending message:>>> ', error);
    }
  }
  catch (e) {
    if (retry == 0) {
      await callSQS(data, 1);
      console.log('sqs send retry >>>');
    }
    else {
      let recieve_error = "Error: " + JSON.stringify(e) + " msgjson: " + JSON.stringify(data);
      console.log('recieve_error', recieve_error);
    }
  }
}

