  const isDisabled = isBulkMode 
    ? (bulkSaveWeekoffMutation.isPending || weekoffData.employeeIds?.length === 0 || !selectedDropdownValue)
    : (saveWeekoffMutation.isPending || (JSON.stringify(oldWeekoffDays.sort((a, b) => a - b)) === JSON.stringify(selectedWeekoffDays.sort((a, b) => a - b))));

  console.log('Button disabled state:', {
    isBulkMode,
    isPending: isBulkMode ? bulkSaveWeekoffMutation.isPending : saveWeekoffMutation.isPending,
    employeeIdsLength: weekoffData.employeeIds?.length,
    selectedDropdownValue,
    isDisabled
  });

  return (
    <>
      <Dropdown.Menu onClick={(e) => {
        // Only stop propagation if not clicking on dropdown elements
        if (!e.target.closest('.p-dropdown')) {
          e.stopPropagation();
        }
      }}>
        <div className="card-header ps-rounded-top-rl p-3">
