import React, { useEffect, useState, useCallback, useMemo } from "react";
import Header from "../../Layouts/Header";
import Dropdown from 'react-bootstrap/Dropdown';
import {
  Col
} from "reactstrap";
import triwarning from "../../assets/images/svg/tri-warning.svg"
import greencircle from "../../assets/images/svg/green-circle.svg"
import Skeleton from 'react-loading-skeleton'
import 'react-loading-skeleton/dist/skeleton.css';
import WeekoffPopup from './WeekoffPopup';
import MyCustomTable from "../../Components/Common/MyCustomTable";
import { useEmployeeWeekoffs } from "../../hooks/useWeekoffQueries";
import EmployeeTag from "../../Components/Common/EmployeeTag";


const WeekoffList = () => {
  const [selectedEmployees, setSelectedEmployees] = useState(new Set());
  const [isSelectAllChecked, setIsSelectAllChecked] = useState(false);

  // Fetch employees
  const { data: employees = [], isLoading: isEmployeeWeekOffFetching } = useEmployeeWeekoffs();

  // Handle individual employee selection
  const handleEmployeeSelect = useCallback((employeeId, checked) => {
    setSelectedEmployees(prev => {
      const newSelection = new Set(prev);
      if (checked) {
        newSelection.add(employeeId);
      } else {
        newSelection.delete(employeeId);
      }
      return newSelection;
    });
  }, []);

  // Handle select all toggle
  const handleSelectAll = useCallback((checked) => {
    setIsSelectAllChecked(checked);
    if (checked) {
      const allEmployeeIds = employees.map(emp => emp.id);
      setSelectedEmployees(new Set(allEmployeeIds));
    } else {
      setSelectedEmployees(new Set());
    }
  }, [employees]);

  // Update select all state based on individual selections
  useEffect(() => {
    if (employees.length > 0) {
      const allSelected = employees.every(emp => selectedEmployees.has(emp.id));
      setIsSelectAllChecked(allSelected);
    }
  }, [selectedEmployees, employees]);

  const [openDropdownId, setOpenDropdownId] = useState(null);
  const [selectedTagsDropdownValue, setSelectedTagsDropdownValue] = useState(null);

  const handleDropdownClick = useCallback((popupId) => {
    if (openDropdownId === popupId) {
      // If clicking the same dropdown that's already open, close it
      setOpenDropdownId(null);
    } else {
      // If clicking a different dropdown or opening for the first time, open it
      setOpenDropdownId(popupId);
    }
  }, [openDropdownId]);

  const closeDropDown = useCallback(() => {
    setOpenDropdownId(null);
  }, []);

  const daysDropDownOptionsItems = [
    { label: 'Sunday', value: 1 },
    { label: 'Monday', value: 2 },
    { label: 'Tuesday', value: 3 },
    { label: 'Wednesday', value: 4 },
    { label: 'Thursday', value: 5 },
    { label: 'Friday', value: 6 },
    { label: 'Saturday', value: 7 }
  ];

  const handleTagsDropdownChange = useCallback((e) => {
    setSelectedTagsDropdownValue(e.value);
  }, []);

  const columns = useMemo(() => {

    const getDayName = {
      1: 'Sunday',
      2: 'Monday',
      3: 'Tuesday',
      4: 'Wednesday',
      5: 'Thursday',
      6: 'Friday',
      7: 'Saturday',
    };

    const getDayColumn = (day, dayIndex) => ({
      Header: day,
      accessor: day.toLowerCase(),
      sortable: false,
      classNameString: (cellData) => {
        const popupId = `${cellData.id}-day-${dayIndex + 1}`;
        const isDropdownOpen = openDropdownId === popupId;
        return isDropdownOpen ? 'ps-weekoff-drop-bg' : '';
      },
      Cell: ({ row }) => {
        const employee = row.original;
        const popupId = `${employee.id}-day-${dayIndex + 1}`;
        const isDropdownOpen = openDropdownId === popupId;

        const weekOffData = JSON.parse(employee.shift_daywise_data) || [];

        const currentDay = weekOffData.find(wo => wo?.day === dayIndex + 1)

        let allDayWorking = true;
        let allDayWeekOff = false;
        let occasionalWeekoffDays = []

        if (currentDay) {
          // If is_weekoff is 1
          allDayWeekOff = currentDay?.is_weekoff === 1;
          // If is_weekoff is 0, it's working day and no occasional_weekoff_days
          allDayWorking = currentDay?.is_weekoff === 0 && (!currentDay?.occasional_weekoff_days || currentDay?.occasional_weekoff_days?.length === 0);

          if (allDayWeekOff) {
            occasionalWeekoffDays = [1, 2, 3, 4, 5];
          } else {
            // If is_weekoff is 1 but has occasional_weekoff_days, it's partial weekoff
            occasionalWeekoffDays = currentDay?.occasional_weekoff_days || [];
          }
        }

        const cursorStyle = 'pointer';

        const dayLabel = () => {

          if (allDayWeekOff) {
            return <p className="mb-0 wo-blue-text">WO</p>;
          } else if (occasionalWeekoffDays.length > 0) {
            return <img src={triwarning} alt="" className="img-fluid" />;
          } else if (allDayWorking) {
            return <img src={greencircle} alt="" className="img-fluid" />;
          } else {
            return <img src={greencircle} alt="" className="img-fluid" />;
          }
        }

        const renderDay = () => {
          return (
            <div className="position-relative cursor-pointer">
              <Dropdown className="ps-weekoff-drop" show={isDropdownOpen} autoClose={false} onClick={() => {
                handleDropdownClick(popupId);
              }}>
                <Dropdown.Toggle>
                  {dayLabel()}
                </Dropdown.Toggle>
                <WeekoffPopup
                  closeDropDown={closeDropDown}
                  dayName={getDayName[dayIndex + 1]}
                  occasionalWeekoffDays={occasionalWeekoffDays}
                  weekoffData={{
                    employeeId: employee.id,
                    employeeName: employee.name,
                    shiftIds: employee.shift_ids?.split(",") || [],
                    day: dayIndex + 1,
                  }}
                />
              </Dropdown>
            </div>
          )
        }

        return (
          <div
            key={popupId + dayIndex}
            // onClick={handleClick}
            style={{ cursor: cursorStyle }}
          >
            {renderDay()}
          </div>
        );
      }
    });

    return [
      {
        Header: <div className="ps-checkbox no-text">
          <input 
            type="checkbox" 
            id="checkall" 
            checked={isSelectAllChecked}
            onChange={(e) => handleSelectAll(e.target.checked)}
            name='select_emp' 
          />
          <label htmlFor="checkall"></label>
        </div>,
        accessor: "select_emp",
        Filter: false,
        Cell: (cellProps) => {
          const { original } = cellProps.row;
          const employeeId = original.id;
          const uniqueId = `emp_${employeeId}`;
          return (
            <div className="ps-checkbox no-text">
              <input 
                type="checkbox" 
                id={uniqueId}
                checked={selectedEmployees.has(employeeId)}
                onChange={(e) => handleEmployeeSelect(employeeId, e.target.checked)}
                name='select_emp' 
              />
              <label htmlFor={uniqueId}></label>
            </div>
          );
        },
      },
      {
        Header: "Name",
        accessor: "name",
        sortable: true,
        Cell: ({ row }) => (
          <div>
            {row.original.name}
            <EmployeeTag tag={row.original.employee_status} />
          </div>
        ),
      },
      {
        Header: "Department",
        accessor: "department",
        sortable: true,
        Cell: ({ row }) => {
          return (
            <div>{row.original.department}</div>
          )
        },
      },
      ...Object.values(getDayName).map((day, index) => getDayColumn(day, index))
    ];
  }, [openDropdownId, closeDropDown, handleDropdownClick, selectedEmployees, isSelectAllChecked, handleEmployeeSelect, handleSelectAll]);

  document.title = "Weekoff | Petpooja Payroll";

  return (
    <React.Fragment>
      <Header title="Week off Assignment" />
      <div className="page-content">
        <div className="container-fluid">
          <div className="row">
            <div className="col-lg-12">
              <div className="card week-off-wrapper ps-border ps-rounded-10 shadow-none">
                <div className="card-header ps-border-bottom ps-rounded-top-rl px-4">
                  <div className="pr-page-title ps-emp-total">
                    <h5 className="mb-1">Assign Week Off</h5>
                    <p className="paragraph-sm-1">Click on the cell icon to handle week offs (WO) </p>
                  </div>
                </div>
                <div className="card-body p-4 pt-0">
                  <div className="d-flex justify-content-end align-items-center flex-wrap">

                    <Dropdown className="ps-weekoff-drop" show={openDropdownId === 'tags-dropdown'} autoClose={false}>
                      <button onClick={() => handleDropdownClick('tags-dropdown')} type="button" className="ps-btn sm-btn grey-outline-btn my-2">Bulk Update</button>
                      <WeekoffPopup
                        closeDropDown={closeDropDown}
                        dayName="Bulk Update"
                        occasionalWeekoffDays={[]}
                        weekoffData={{
                          employeeIds: Array.from(selectedEmployees),
                          employeeName: `${selectedEmployees.size} employees`,
                          shiftIds: [],
                          day: null,
                        }}
                        dropdownOptions={daysDropDownOptionsItems}
                        selectedDropdownValue={selectedTagsDropdownValue}
                        onDropdownChange={handleTagsDropdownChange}
                      />
                    </Dropdown>
                    <div className="float-md-end indicator-bg py-1 py-md-1 px-3 my-3 ms-3">
                      <div className="d-flex justify-content-end align-items-center">
                        <p className="mb-0 tx-black fw-500 ms-md-3 mb-1 mb-md-0">
                          <span className="me-1">
                            <span className="mb-0 wo-blue-text">WO</span>
                          </span>Week Off
                        </p>
                        <p className="mb-0 tx-black fw-500 ms-md-3 mb-1 mb-md-0">
                          <span className="me-1">
                            <img src={greencircle} alt="" className="img-fluid" />
                          </span>Working Day
                        </p>
                        <p className="mb-0 ms-md-3 tx-black fw-500 mb-1 mb-md-0">
                          <span className="me-1">
                            <img src={triwarning} alt="" className="img-fluid" />
                          </span>Occasional Week Off
                        </p>
                      </div>
                    </div>
                  </div>

                  <div>

                    <div className="table-responsive w-100">

                      <Col xl={12}>
                        {!isEmployeeWeekOffFetching ? (
                          <MyCustomTable
                            columns={columns}
                            data={employees}
                            getRowProps={(row) => ({
                              className: row.original.isPrimary ? 'ps-dashed-border-bottom' : ''
                            })}
                            autoResetPage={false}
                          />
                        ) : (
                          <div className="">
                            <Skeleton count={8} height={60} />
                          </div>
                        )}
                      </Col>

                    </div>
                  </div>

                </div>
              </div>
            </div>

          </div>
        </div>
      </div>

    </React.Fragment>
  );
}

export default WeekoffList;

