                            className={classnames("page-item ps-pagination-prev", (pageIndex === 0) ? 'disabled' : '')}
                            to="#"
                            onClick={() => { previousPage(); (onPageChange !== false && onPageChange()); }}
                        >
                            Previous
                        </Link>
                        <ul className="pagination ps-listjs-pagination mb-0">
                            {Array.from({ length: Math.min(pageCount, 5) }, (_, index) => {
                                let pageOffset = Math.floor(5 / 2);
                                let startPage = Math.max(0, Math.min(pageIndex - pageOffset, pageCount - 5));
                                let endPage = Math.min(pageCount, startPage + 5);
                                return (
                                    index < endPage - startPage &&
                                    <li key={startPage + index} onClick={() => { gotoPage(startPage + index); (onPageChange !== false && onPageChange()); }} className={(pageIndex === startPage + index) ? 'active' : ''}>
                                        <Link className="page" to="#">
                                            {startPage + index + 1}
                                        </Link>
                                    </li>
                                );
                            })}
                        </ul>
                        <Link
                            className={classnames("page-item ps-pagination-next", (pageIndex === pageCount - 1) ? 'disabled' : '')}
                            to="#"
                            onClick={() => { nextPage(); (onPageChange !== false && onPageChange()); }}
                        >
                            Next
                        </Link>
                    </div>
                </div>
                : ''
            }

        </>
    );
};

export default MyCustomTable;

