                  'branch_ids': employees_data[e]?.branch_ids ? employees_data[e].branch_ids.split(',') : [],
                  'mobile_device_id' : (employees_data[e].mobile_device_id !== null) ? employees_data[e].mobile_device_id : "",
                  'attendance_method' : (employees_data[e].attendance_method !== null) ? employees_data[e].attendance_method : 0,
                  'face_info' : (employees_data[e].face_info !== null) ? employees_data[e].face_info : "",
                  'isSalaryTypeEditable': false
                };

                org_employees.push(employee_obj);
              }

              response_status = 1;
              response_message = 'employees found';
              response_data = request_body.id === undefined ? org_employees : org_employees[0];
              response_code = 200;

            } else {
              response_status = 0;
              response_message = 'No employees found in this organization';
              response_code = 200;
            }

        // employee list page
      }
    } else {
      response_status = validate_request.status;
      response_message = validate_request.msg;
      response_code = 400;
    }
  } catch (e) { 
    let error_obj ={"errorlog":e.stack,"request_data":event};
    await send_error(error_obj);
    response_status = 0;
    response_message = 'Internal Server Error';
    response_code = 500;

  } finally {
    console.log('finally block called...');
    await transactionRelease(db);
  }

  api_response = {'status' : response_status, 'message' : response_message, 'statusCode' : response_code};

  if(Object.keys(response_data).length > 0) {
    api_response.data = response_data;
  }

  return api_response;
}


function customFormatDate(date,interval = 0,setTimeZome = 'Asia/Kolkata') {
    date = new Date(date.toLocaleString("en-US", {timeZone: setTimeZome}));
    if(interval > 0) {
        date.setSeconds(date.getSeconds() + parseInt(interval,10));
    }
    let final_date = date.toISOString().substring(0, 10);
    let hours = date.getHours();
    hours = (hours <= 9) ? '0' + hours : hours;
    let minutes = date.getMinutes();
    minutes = (minutes <= 9) ? '0' + minutes : minutes;
    let seconds = date.getSeconds();
    seconds = (seconds <= 9) ? '0' + seconds : seconds;
    let final_time = hours + ":" + minutes + ":" + seconds;

    let monthsname = ["Jan","Feb","Mar","April","May","June","July","Aug","Sep","Oct","Nov","Dec"];

    let ddmyyyy = date.getDate()+' '+monthsname[date.getMonth()]+' '+date.getFullYear();

    return {"date": final_date,"time": final_time,"date_formatted": final_date + ' ' + final_time, "ddmyyyy" : ddmyyyy};
}

const mysql = require('mysql');

const SecretsManager = require('./secretsManager.js');
const jwtTokenLayer = require('/opt/nodejs/app.js');

var pool;

async function getConnection(){
  return new Promise((resolve, reject) => {
      pool.getConnection(function(err, connection) {
          if(err) {
               //console.log('Error while getting connection');
               reject();
          }
          resolve(connection);
      });
  });
}

async function transactionRelease(connection){
  return new Promise((resolve, reject) => {
      connection.destroy();

      if (connection.state === 'disconnected') {
          console.log('Connection destroyed successfully.');
      } else {
          console.log('Connection released back to pool successfully.');
      }
      resolve();
  });
}

function execQuery(query,connection) {
    return new Promise((resolve, reject) => {
        connection.query(query , function(err, result, fields) {
            if (err) {
                console.log("Error IN query : ", query);
                reject(err);
            } else {
                resolve(result);
            }    
        });
    });
}

function send_error(msg) {
  return new Promise(async(resolve, reject) => {
    console.log(msg);
    resolve();
  });
}

function jsonEscape(str)  {
    return str.replace(/\\n/g, "\n").replace(/\r/g, "\\\\r").replace(/\t/g, "\\\\t");
}

