const mysql = require('mysql');

const SecretsManager = require('./secretsManager.js');
const jwtTokenLayer = require('/opt/nodejs/app.js');

var pool;

async function getConnection(){
  return new Promise((resolve, reject) => {
      pool.getConnection(function(err, connection) {
          if(err) {
               //console.log('Error while getting connection');
               reject();
          }
          resolve(connection);
      });
  });
}

async function transactionRelease(connection){
  return new Promise((resolve, reject) => {
      connection.destroy();

      if (connection.state === 'disconnected') {
          console.log('Connection destroyed successfully.');
      } else {
          console.log('Connection released back to pool successfully.');
      }
      resolve();
  });
}

function execQuery(query,connection) {
    return new Promise((resolve, reject) => {
        connection.query(query , function(err, result, fields) {
            if (err) {
                console.log("Error IN query : ", query);
                reject(err);
            } else {
                resolve(result);
            }    
        });
    });
}

function send_error(msg) {
  return new Promise(async(resolve, reject) => {
    console.log(msg);
    resolve();
  });
}

function jsonEscape(str)  {
    return str.replace(/\\n/g, "\n").replace(/\r/g, "\\\\r").replace(/\t/g, "\\\\t");
}

exports.handler = async (event, context) => {
  //use secret manager

  if (typeof pool === 'undefined') {
    var secretName = process.env.payroll_secret_name;
    var region = process.env.payroll_secret_re;
    var secret_manager_value = await SecretsManager.getSecret(secretName, region);

    var secret_manager_result;
    if (typeof secret_manager_value === 'string') {
        secret_manager_result = JSON.parse(secret_manager_value);
    } else {
        secret_manager_result = secret_manager_value;
    }

    var payroll_config = {
        database: secret_manager_result.dbname,
        user: secret_manager_result.username,
        password: secret_manager_result.password,
        host: secret_manager_result.host
    };
    pool = mysql.createPool(payroll_config);
  }

  //use secret manager


  var db = await getConnection();
  let api_response = {};
  let response_status;
  let response_message;
  let response_data = {};
  let response_code;

  try {
    let request_headers = JSON.parse(JSON.stringify(event.headers));
    let request_body = JSON.parse(JSON.stringify(event.body));

        console.log( 'request_body >>>>>>>>>>>>>>>>>',request_body);


    let validate_request = await jwtTokenLayer.validate_user_request(request_headers, request_body);



    console.log( 'validate_request >>>>>>>>>>>>>>>>>',validate_request);

    if (validate_request.status === 1) { //access_token match
      let method = request_body.method;

      request_body.org_id = validate_request.user_data.org_id;
      request_body.ho_id = validate_request.user_data.ho_id;
      request_body.user_id = validate_request.user_data.user_id
      request_body.organization_id = validate_request.user_data.org_id;
      request_body.user_organizations = validate_request.user_data.user_organizations ?? '';
      request_body.user_type = validate_request.user_data.user_type ?? '';
      request_body.department_ids = validate_request.user_data.department_ids ?? null;



      let conditionField = (request_body.org_id === 0) ? 'ho_id' : 'ho_id';
      let conditionValue = (request_body.org_id === 0) ? request_body.ho_id : request_body.ho_id;

      if(method === 'get_emp_related_data') {
        let org_departments = [];
        let org_designations = {};
        let org_designations_only = [];
        let org_next_empid;
        let departmentCondition = ''

        if (request_body.user_type !== 1 && request_body.department_ids !== null) {
          if (request_body.department_ids) {
            departmentCondition = `AND id IN (${request_body.department_ids})`;
          } else {
            departmentCondition = `AND id IS NULL`;
          }
        }

        let org_departments_res = await execQuery(`SELECT name,id from departments WHERE ho_id = ${request_body.ho_id} AND status = 1 ${departmentCondition}`, db);

        if(org_departments_res.length > 0) {
          let od;
          for(od = 0; od < org_departments_res.length; od++) { 
            let tmpObj = {
              label : org_departments_res[od].name,
              value : org_departments_res[od].id
            }

            org_departments.push(tmpObj);
          }
        }

        let org_designations_res = await execQuery(`SELECT name,id,department_id from designations WHERE ho_id = ${request_body.ho_id} AND status = 1`, db);

        if(org_designations_res.length > 0) {
          let ods;
          for(ods = 0; ods < org_designations_res.length; ods++) { 
            let dept_id = org_designations_res[ods].department_id;

            if(org_designations[dept_id] === undefined) {
              org_designations[dept_id] = [];    
            }

            let tmpObj = {
              label : org_designations_res[ods].name,
              value : org_designations_res[ods].id
            }

            org_designations[dept_id].push(tmpObj);

            //independent designations

            let tmpObjTwo = {
              label : org_designations_res[ods].name,
              value : org_designations_res[ods].id
            }

            org_designations_only.push(tmpObjTwo);
          }
