              'mobile_number' : employee_res[0].mobile_no,
              'pan_no' : (employee_res[0].pan_no !== null) ? employee_res[0].pan_no : '',
              'address' : employee_res[0].address,
              'designation_id' : (employee_res[0].designation_id !== null) ? employee_res[0].designation_id : '',
              'department_id' : (employee_res[0].department_id !== null) ? employee_res[0].department_id : '',
              'driving_lic' : (employee_res[0].driving_lic !== null) ? employee_res[0].driving_lic : '',
              'photo' : (employee_res[0].photo !== null) ? employee_res[0].photo : '',
              'code' : (employee_res[0].code !== null) ? employee_res[0].code : '',
