  1230                        >
  1231                          <Worksheet
  1232                            key={spreadsheetKey}
  1233                            freezeColumns="2"
  1234                            data={spreadsheetData}
  1235                            columns={spreadsheetColumns}
  1236                            nestedHeaders={NESTED_HEADERS}
  1237                            defaultColAlign="center"
  1238                            search={false}
  1239                            tableOverflow={true}
  1240                            allowInsertRow={false}
  1241                            allowInsertColumn={false}
  1242                            allowDeletingRows={false}
  1243                            rowResize={false}
