  }, [calculateColumnWidths]);

  const columnIndexMap = useMemo(() => ({
    "Emp ID": 0,
    "Name": 1,
    "Department": 2,
    "Designation": 3,
    "Professional Tax": 4,
    "PT Amount": 5,
    "Labour Welfare Fund": 6,
    "LWF Amount": 7,
  }), []);

  const { ptIndex, ptAmountIndex, lwfIndex, lwfAmountIndex } = useMemo(() => ({
    ptIndex: columnIndexMap["Professional Tax"],
    ptAmountIndex: columnIndexMap["PT Amount"],
    lwfIndex: columnIndexMap["Labour Welfare Fund"],
    lwfAmountIndex: columnIndexMap["LWF Amount"]
  }), [columnIndexMap]);

  const { ptRelatedCells, lwfRelatedCells } = useMemo(() => ({
    ptRelatedCells: [ptIndex, ptAmountIndex],
    lwfRelatedCells: [lwfIndex, lwfAmountIndex]
  }), [ptIndex, ptAmountIndex, lwfIndex, lwfAmountIndex]);

  const setCellStyle = useCallback((worksheet, cellIndexes, rowIndex, styleProperty, styleValue) => {
    if (!worksheet || !Array.isArray(cellIndexes) || rowIndex === undefined) return;
    try {
      const existingStyles = worksheet.getStyle() || {};
      const newStyles = { ...existingStyles };

      for (let i = 0; i < cellIndexes.length; i++) {
        const index = cellIndexes[i];
        if (index === undefined || index < 0) continue;

        const cellPosition = `${getColumnName(index)}${parseInt(rowIndex) + 1}`;

        if (styleValue) {
          newStyles[cellPosition] = `${styleProperty}: ${styleValue}`;
        } else {
          delete newStyles[cellPosition];
        }
      }

      worksheet.setStyle(newStyles);
    } catch (error) {
      console.error("Error setting cell styles:", error);
    }
  }, []);

  const setCellsReadOnly = useCallback((worksheet, indexes, rowIndex, isReadOnly) => {
    if (!worksheet || !Array.isArray(indexes) || rowIndex === undefined) return;

    try {
      for (let i = 0; i < indexes.length; i++) {
        const index = indexes[i];
        if (index < 0 || !baseColumns[index]) continue;

        worksheet.setProperty(index, rowIndex, {
          ...baseColumns[index],
          readOnly: isReadOnly
        });
      }
    } catch (error) {
      console.error("Error setting readOnly property:", error);
    }
  }, [baseColumns]);

  const transformRowDataToObject = useCallback((rowData, columns) => {
    if (!rowData || !columns || !Array.isArray(rowData) || !Array.isArray(columns)) {
      return {};
    }

    try {
      const values = rowData.map((cell) => cell?.v);
      const keys = columns.map((col) => col?.title);
      const result = _.zipObject(keys, values);
      return result;
    } catch (error) {
      console.error("Error transforming row data:", error);
      return {};
    }
  }, []);

  const processApiDataForSpreadsheet = useCallback((configsData) => {
    if (!configsData) return { data: [], columns: [] };

    try {
      const orgConfig = configsData[API_KEYS.ORG_CONFIG] || {};
      const employeeKeys = Object.keys(configsData).filter(key => key !== API_KEYS.ORG_CONFIG);
      const dataRows = new Array(employeeKeys.length);

      for (let i = 0; i < employeeKeys.length; i++) {
        const empKey = employeeKeys[i];
        const emp = configsData[empKey];
        if (!emp) continue;

        const isEntryExistInDb = emp?.[API_KEYS.CONFIG_ID] && parseInt(emp[API_KEYS.CONFIG_ID]) > 0;

        dataRows[i] = {
          "Emp ID": emp[API_KEYS.CODE] || "",
          "Name": emp[API_KEYS.EMPLOYEE_NAME] || "",
          "Department": emp[API_KEYS.DEPARTMENT] || "",
          "Designation": emp[API_KEYS.DESIGNATION] || "",
          "Professional Tax": isEntryExistInDb ? (emp?.[API_KEYS.PT] === 1 ? "On" : "Off") : (orgConfig?.[API_KEYS.PT] === 1 ? "On" : "Off"),
          "PT Amount": isEntryExistInDb ? (emp?.[API_KEYS.PT_AMOUNT] || 0) : (orgConfig?.[API_KEYS.PT_AMOUNT] || 0),
          "Labour Welfare Fund": isEntryExistInDb ? (emp?.[API_KEYS.LWF] === 1 ? "On" : "Off") : (orgConfig?.[API_KEYS.LWF] === 1 ? "On" : "Off"),
          "LWF Amount": isEntryExistInDb ? (emp?.[API_KEYS.LWF_AMOUNT] || 0) : (orgConfig?.[API_KEYS.LWF_AMOUNT] || 0),
        };
      }

      const filteredRows = dataRows.filter(Boolean);

      return { 
        data: filteredRows, 
        columns: baseColumns
      };
    } catch (error) {
      console.error("Error processing API data:", error);
      return { data: [], columns: [] };
    }
  }, [baseColumns]);

  const updateRelatedFieldsData = useCallback((rowIndex, worksheet, toggleData) => {
    if (!worksheet || rowIndex === undefined) return;

    try {
      const { toggleField, oldValue, newValue, relatedFields, cellIndexes, isToggleOn } = toggleData;

      if (Array.isArray(relatedFields) && relatedFields.length > 0) {
        for (let i = 0; i < relatedFields.length; i++) {
          const field = relatedFields[i];
          if (field.index !== undefined && field.index >= 0) {
            worksheet.setValueFromCoords(field.index, rowIndex, field.value);
          }
        }
      }

      setCellStyle(worksheet, cellIndexes, rowIndex, "color", isToggleOn ? THEME.ACTIVE_COLOR : THEME.DEFAULT_COLOR);

      if (Array.isArray(cellIndexes) && cellIndexes.length > 1) {
        const dependentFields = cellIndexes.slice(1);
        setCellsReadOnly(worksheet, dependentFields, rowIndex, !isToggleOn);
      }

      setModifiedData(prev => {
        const rowData = worksheet?.records ? worksheet.records[rowIndex] : null;
        if (!rowData) return prev;

        const rowDataObject = transformRowDataToObject(rowData, spreadsheetColumns);
        const existingRowIndex = prev.findIndex(item => item.rowIndex === String(rowIndex));

        const changes = {
          [toggleField]: { newValue, oldValue }
        };

        if (Array.isArray(relatedFields)) {
          for (const field of relatedFields) {
            if (field.name) {
              changes[field.name] = { 
                newValue: field.value, 
                oldValue: rowDataObject[field.name] 
              };
            }
          }
        }

        if (existingRowIndex >= 0) {
          const updatedData = [...prev];
          updatedData[existingRowIndex] = {
            ...updatedData[existingRowIndex],
            rowData: rowDataObject,
            changes: { ...updatedData[existingRowIndex].changes, ...changes }
          };
          return updatedData;
        } else {
          return [
            ...prev,
            { rowIndex: String(rowIndex), rowData: rowDataObject, changes }
          ];
        }
      });
    } catch (error) {
      console.error("Error updating related fields:", error);
    }
  }, [setCellStyle, setCellsReadOnly, transformRowDataToObject, spreadsheetColumns]);

  const trackCellChange = useCallback((worksheet, colIndex, rowIndex, newValue, oldValue) => {
    if (newValue === oldValue || !worksheet) return;

    try {
      setModifiedData(prev => {
        const rowData = worksheet.records && worksheet.records[rowIndex];
        if (!rowData) return prev;

        const columnTitle = spreadsheetColumns[colIndex]?.title;
        if (!columnTitle) return prev;

        const rowDataObject = transformRowDataToObject(rowData, spreadsheetColumns);
        const existingRowIndex = prev.findIndex(item => item.rowIndex === String(rowIndex));

        if (existingRowIndex >= 0) {
          const updatedRow = {
            ...prev[existingRowIndex],
            rowData: rowDataObject,
            changes: {
              ...prev[existingRowIndex].changes,
              [columnTitle]: { newValue, oldValue },
            },
          };

          const newArray = [...prev];
          newArray[existingRowIndex] = updatedRow;
          return newArray;
        } else {
          return [
            ...prev,
            {
              rowIndex: String(rowIndex),
              rowData: rowDataObject,
              changes: {
                [columnTitle]: { newValue, oldValue },
              },
            },
          ];
        }
      });
    } catch (error) {
      console.error("Error tracking cell change:", error);
    }
  }, [spreadsheetColumns, transformRowDataToObject]);

  const processPTToggle = useCallback((worksheet, rowIndex, newValue, oldValue) => {
    if (!worksheet || rowIndex === undefined) return;

    const isToggleOn = newValue === "On";

    try {
      if (!isToggleOn) {
        updateRelatedFieldsData(rowIndex, worksheet, {
          toggleField: "Professional Tax",
          oldValue,
          newValue,
          isToggleOn,
          cellIndexes: ptRelatedCells,
          relatedFields: [
            { index: ptAmountIndex, name: "PT Amount", value: 0 }
          ]
        });
      } else {
        setCellStyle(worksheet, ptRelatedCells, rowIndex, "color", THEME.ACTIVE_COLOR);
        const dependentFields = ptRelatedCells.slice(1);
        setCellsReadOnly(worksheet, dependentFields, rowIndex, false);
        trackCellChange(worksheet, ptIndex, rowIndex, newValue, oldValue);
      }
    } catch (error) {
      console.error("Error processing PT toggle:", error);
    }
  }, [ptAmountIndex, ptRelatedCells, ptIndex, updateRelatedFieldsData, setCellStyle, setCellsReadOnly, trackCellChange]);

  const processLWFToggle = useCallback((worksheet, rowIndex, newValue, oldValue) => {
    if (!worksheet || rowIndex === undefined) return;

    const isToggleOn = newValue === "On";

    try {
      if (!isToggleOn) {
        updateRelatedFieldsData(rowIndex, worksheet, {
          toggleField: "Labour Welfare Fund",
          oldValue,
          newValue,
          isToggleOn,
          cellIndexes: lwfRelatedCells,
          relatedFields: [
            { index: lwfAmountIndex, name: "LWF Amount", value: 0 }
          ]
        });
      } else {
        setCellStyle(worksheet, lwfRelatedCells, rowIndex, "color", THEME.ACTIVE_COLOR);
        const dependentFields = lwfRelatedCells.slice(1);
        setCellsReadOnly(worksheet, dependentFields, rowIndex, false);
        trackCellChange(worksheet, lwfIndex, rowIndex, newValue, oldValue);
      }
    } catch (error) {
      console.error("Error processing LWF toggle:", error);
    }
  }, [lwfAmountIndex, lwfRelatedCells, lwfIndex, updateRelatedFieldsData, setCellStyle, setCellsReadOnly, trackCellChange]);

  const validateData = useCallback(() => {
    if (!spreadsheetData || !Array.isArray(spreadsheetData) || spreadsheetData.length === 0) {
      return false;
    }

    try {
      const latestData = _.cloneDeep(spreadsheetData);
      const invalidRows = [];

      if (Array.isArray(modifiedData) && modifiedData.length > 0) {
        modifiedData.forEach(item => {
          const index = parseInt(item.rowIndex);
          if (isNaN(index) || index < 0 || index >= latestData.length) return;

          if (item.changes) {
            Object.entries(item.changes).forEach(([columnName, { newValue }]) => {
              if (latestData[index] && columnName in latestData[index]) {
                latestData[index][columnName] = newValue;
              }
            });
          }
        });
      }
      let isValid = true;

      latestData.forEach((row, index) => {
        if (!row) return;

        if (row["Professional Tax"] === "On") {
          const ptAmount = parseFloat(row["PT Amount"]) || 0;

          if (ptAmount <= 0) {
            isValid = false;
            invalidRows.push({
              index,
              employee: row["Name"] || `Row ${index + 1}`,
              issue: "PT amount is required when Professional Tax is On"
            });
          }
        }

        if (row["Labour Welfare Fund"] === "On") {
          const lwfAmount = parseFloat(row["LWF Amount"]) || 0;

          if (lwfAmount <= 0) {
            isValid = false;
            invalidRows.push({
              index,
              employee: row["Name"] || `Row ${index + 1}`,
              issue: "LWF amount is required when Labour Welfare Fund is On"
            });
          }
        }
      });

      if (!isValid) {
        console.warn("Validation failed with the following issues:", invalidRows);
        toast.error(MESSAGES.VALIDATION_ERROR);
      }

      return isValid;
    } catch (error) {
      console.error("Error validating data:", error);
      toast.error(MESSAGES.UNEXPECTED_ERROR);
      return false;
    }
  }, [spreadsheetData, modifiedData]);

  const prepareDataForSave = useCallback(() => {
    if (!modifiedData || modifiedData.length === 0 || !empConfigsData || !spreadsheetData) {
      return null;
    }

    try {
      const changes = {};

      const modifiedRows = modifiedData
        .map(item => ({ 
          rowIndex: parseInt(item.rowIndex), 
          data: item 
        }))
        .sort((a, b) => a.rowIndex - b.rowIndex);

      for (const { rowIndex, data } of modifiedRows) {
        const rowData = spreadsheetData[rowIndex];
        if (!rowData) continue;

        const employeeCode = rowData["Emp ID"];
        if (!employeeCode || employeeCode === "Org Configs") continue;

        const employeeEntry = Object.entries(empConfigsData).find(
          ([key, value]) => key !== API_KEYS.ORG_CONFIG && value[API_KEYS.CODE]?.toString() === employeeCode?.toString()
        );

        if (!employeeEntry) continue;
        const [, employeeRecord] = employeeEntry;
        const employeeId = employeeRecord[API_KEYS.ROW_ID];

        if (!changes[employeeId]) {
          changes[employeeId] = {
            user_id: employeeId,
            employee_id: employeeId,
            employee_name: rowData["Name"],
            professional_tax: rowData["Professional Tax"] === "On" ? 1 : 0,
            pt_amount: parseFloat(rowData["PT Amount"]) || 0,
            labour_welfare_fund: rowData["Labour Welfare Fund"] === "On" ? 1 : 0,
            lwf_amount: parseFloat(rowData["LWF Amount"]) || 0
          };

          if (employeeRecord.statutory_compliance_id) {
            changes[employeeId].id = employeeRecord.statutory_compliance_id;
          }
        }

        if (data.changes) {
          const skipColumns = new Set(["Emp ID", "Name", "Code", "Department", "Designation"]);
          const toggleColumns = new Set(["Professional Tax", "Labour Welfare Fund"]);

          Object.entries(data.changes).forEach(([columnName, { newValue }]) => {
            if (toggleColumns.has(columnName)) {
              let apiFieldName = "";

              if (columnName === "Professional Tax") {
                apiFieldName = "professional_tax";
              } else if (columnName === "Labour Welfare Fund") {
                apiFieldName = "labour_welfare_fund";
              }
              changes[employeeId][apiFieldName] = newValue === "On" ? 1 : 0;
            } else if (!skipColumns.has(columnName)) {
              let apiFieldName = "";

              if (columnName === "PT Amount") {
                apiFieldName = "pt_amount";
              } else if (columnName === "LWF Amount") {
                apiFieldName = "lwf_amount";
              }

              if (apiFieldName) {
                changes[employeeId][apiFieldName] = parseFloat(newValue) || 0;
              }
            }
          });
        }
      }

      return Object.keys(changes).length > 0 
        ? { 
            changes, 
            effective_date_from: dateRange.start, 
            effective_date_to: dateRange.end,
            config_name: 'pt_and_lwf'
          } 
        : null;
    } catch (error) {
      console.error("Error preparing data for save:", error);
      return null;
    }
  }, [modifiedData, empConfigsData, spreadsheetData, dateRange]);

  const handleSave = useCallback(() => {
    if (isSaving || !modifiedData || modifiedData.length === 0) return;

    try {
      setIsSaving(true);

      const isValid = validateData();
      if (!isValid) {
        setIsSaving(false);
        return;
      }

      const payload = prepareDataForSave();
      if (!payload) {
        toast.warning(MESSAGES.NO_CHANGES);
        setIsSaving(false);
        return;
      }

      mutate(payload);
    } catch (error) {
      console.error("Error saving configuration:", error);
      toast.error(MESSAGES.SAVE_ERROR);
      setIsSaving(false);
    }
  }, [isSaving, modifiedData, validateData, prepareDataForSave, mutate]);

  const initializeSpreadsheet = useCallback((worksheet) => {
    if (!worksheet || !worksheet.records) {
      console.log("Worksheet not available for initialization");
      return;
    }

    try {
      const records = worksheet.records;
      const worksheetCols = worksheet?.cols || []

      for (let i = 0; i < worksheetCols.length; i++) {
        const col = worksheetCols[i];

        if (!Object.hasOwn(col, 'title')) {
          try {
            const cellIndex = col.x;
            const colName = getColumnName(cellIndex );
            if (colName) {
              const colProp = col || {};
              colProp.locked = true
              colProp.readOnly= true
              worksheet.setProperties(colName, colProp);
            }
          } catch (error) {
            console.warn(`Error setting column ${col.x} to read-only:`, error);
          }
        }
      }

      for (let row = 0; row < records.length; row++) {
        if (ptIndex !== -1) {
          const ptValue = worksheet.getValueFromCoords(ptIndex, row);
          const isActive = ptValue === "On";

          setCellStyle(worksheet, ptRelatedCells, row, "color", isActive ? THEME.ACTIVE_COLOR : THEME.DEFAULT_COLOR);
          const ptDependentFields = [ptAmountIndex];
          setCellsReadOnly(worksheet, ptDependentFields, row, !isActive);
        }

        if (lwfIndex !== -1) {
          const lwfValue = worksheet.getValueFromCoords(lwfIndex, row);
          const isActive = lwfValue === "On";
          setCellStyle(worksheet, lwfRelatedCells, row, "color", isActive ? THEME.ACTIVE_COLOR : THEME.DEFAULT_COLOR);
          const lwfDependentFields = [lwfAmountIndex];
          setCellsReadOnly(worksheet, lwfDependentFields, row, !isActive);
        }
      }
    } catch (error) {
      console.error("Error initializing spreadsheet:", error);
    }
  }, [
    ptIndex, 
    ptAmountIndex, 
    ptRelatedCells,
    lwfIndex,
    lwfAmountIndex,
    lwfRelatedCells,
    setCellStyle,
    setCellsReadOnly
  ]);

  const calculateSelectionDetails = useCallback((pxVal, pyVal, uxVal, uyVal) => {
    if (typeof pxVal === "number" && typeof pyVal === "number" && 
        typeof uxVal === "number" && typeof uyVal === "number") {
      const startCol = Math.min(pxVal, uxVal);
      const endCol = Math.max(pxVal, uxVal);
      const startRow = Math.min(pyVal, uyVal);
      const endRow = Math.max(pyVal, uyVal);
      const selectionCount = (endCol - startCol + 1) * (endRow - startRow + 1);
      return { startCol, endCol, startRow, endRow, selectionCount };
    }
    return null;
  }, []);

  const validateToggleColumnPaste = useCallback((spreadsheetInstance) => {
    if (!spreadsheetInstance.paste) return;

    const originalPasteFunction = spreadsheetInstance.paste;
    const toggleColumns = new Set([ptIndex, lwfIndex]);
    const amountColumns = new Set([ptAmountIndex, lwfAmountIndex]);

    spreadsheetInstance.paste = function(data, ...args) {
      try {
        const selectedColumns = spreadsheetInstance?.getSelectedColumns();
        if (!selectedColumns) return originalPasteFunction.call(spreadsheetInstance, data, ...args);

        const isTargetingToggleColumn = selectedColumns?.some(columnIndex => toggleColumns.has(columnIndex));
        const isTargetingAmountColumn = selectedColumns?.some(columnIndex => amountColumns.has(columnIndex));

        if (isTargetingToggleColumn || isTargetingAmountColumn) {
          let pastedValue = "";
          if (typeof data === 'number' && args.length > 1 && typeof args[1] === 'string') {
            pastedValue = args[1].trim().replace(/\r?\n$/, '');
          } else if (typeof data === 'string') {
            pastedValue = data.trim();
          } else {
            pastedValue = String(data).trim();
          }

          const isValidToggleValue = pastedValue === "On" || pastedValue === "Off";
          const isValidAmountValue = !isNaN(pastedValue);

          if (!isValidToggleValue && isTargetingToggleColumn) {
            toast.warning(`Only "On" or "Off" values can be pasted in toggle columns.`);
            return false;
          }

          if (!isValidAmountValue && isTargetingAmountColumn) {
            toast.warning(`Please enter numeric values on amount fields`);
            return false;
          }
        }

        return originalPasteFunction.call(spreadsheetInstance, data, ...args);
      } catch (error) {
        console.error("Error validating paste operation:", error);
        toast.warning("Could not validate paste content.");
        return false;
      }
    };
  }, [ptIndex, lwfIndex, ptAmountIndex, lwfAmountIndex]);

  const initializeSpreadsheetWithCustomBehaviors = useCallback((worksheetObject) => {
    if (!worksheetObject?.worksheets || !worksheetObject.worksheets[0]) {
      console.warn("Worksheet object not available for initialization");
      return;
    }

    const activeWorksheet = worksheetObject.worksheets[0];
    if (activeWorksheet.hideIndex) { activeWorksheet.hideIndex(); }
    initializeSpreadsheet(activeWorksheet);
    validateToggleColumnPaste(activeWorksheet);
  }, [initializeSpreadsheet, validateToggleColumnPaste]);

  const processMultiCellCopyOperation = useCallback((worksheetObject, selectionCoordinates) => {
    if (!selectionCoordinates || !Array.isArray(selectionCoordinates)) return;

    const [startColPosition, startRowPosition, endColPosition, endRowPosition] = selectionCoordinates;
    const selectionDetails = calculateSelectionDetails(
      startColPosition, startRowPosition, endColPosition, endRowPosition
    );

    if (selectionDetails && selectionDetails.selectionCount > 1) {
      worksheetObject.updateSelectionFromCoords(
        selectionDetails.startCol, 
        selectionDetails.startRow, 
        selectionDetails.startCol, 
        selectionDetails.startRow
      );
      worksheetObject.copy();
    }
  }, [calculateSelectionDetails]);

  const processCellValueChange = useCallback((worksheetObject, columnIndexStr, rowIndexValue, newCellValue, previousValue) => {
    const columnNumber = parseInt(columnIndexStr, 10);
    const isToggleControlColumn = columnNumber === ptIndex || columnNumber === lwfIndex;

    if (isRestoringDropdownValueRef.current) {
      return;
    }

    const isEmptyValueBug = isToggleControlColumn && ((!newCellValue || newCellValue === "") && previousValue);

    if (isEmptyValueBug) {
      isRestoringDropdownValueRef.current = true;
      setTimeout(() => {
        worksheetObject.setValueFromCoords(columnNumber, rowIndexValue, previousValue);
        setTimeout(() => {
          isRestoringDropdownValueRef.current = false;
        }, 50);
      }, 100);
    } else {
      if (columnNumber === ptIndex) {
        processPTToggle(worksheetObject, rowIndexValue, newCellValue, previousValue);
      } else if (columnNumber === lwfIndex) {
        processLWFToggle(worksheetObject, rowIndexValue, newCellValue, previousValue);
      } else {
        trackCellChange(worksheetObject, columnIndexStr, rowIndexValue, newCellValue, previousValue);
      }
    }
  }, [
    ptIndex,
    lwfIndex,
    trackCellChange,
    processPTToggle,
    processLWFToggle
  ]);

  const globalEventHandler = useCallback((eventName, worksheet, section, colIndex, rowIndex, newValue, oldValue) => {
    try {
      switch (eventName) {
        case "onload":
          return initializeSpreadsheetWithCustomBehaviors(worksheet);
        case "oncopy":
          return processMultiCellCopyOperation(worksheet, section);
        case "onchange":
          return processCellValueChange(worksheet, colIndex, rowIndex, newValue, oldValue);
        default:
          return;
      }
    } catch (error) {
      console.error("Error handling spreadsheet event:", error);
    }
  }, [initializeSpreadsheetWithCustomBehaviors, processMultiCellCopyOperation, processCellValueChange]);

  useEffect(() => {
    if (dateRange.start && dateRange.end) {
      dispatch(
        getEmployeeConfigsData({
          effective_date_from: dateRange.start,
          effective_date_to: dateRange.end,
          config_name: 'pt_and_lwf'
        })
      );
    }
  }, [dateRange, dispatch]);

  useEffect(() => {
    if (location.state) {
      if (location.state.effective_date_from && location.state.effective_date_to) {
        setDateRange({
          start: location.state.effective_date_from,
          end: location.state.effective_date_to
        });
      } else if (location.state.from && location.state.to) {
        setDateRange({
          start: location.state.from,
          end: location.state.to
        });
      }

      if (location.state.header) {
        setPageHeader(location.state.header);
      }
    }
  }, [location]);

  useEffect(() => {
    if (!_.isEmpty(empConfigsData) && empConfigsData !== prevEmpConfigsDataRef.current) {
      try {
        prevEmpConfigsDataRef.current = empConfigsData;
        setShowSpreadsheet(false);
        setIsStylesReady(false);
        spreadsheetRef.current = null;

        const result = processApiDataForSpreadsheet(empConfigsData);

        setSpreadsheetData(result.data);
        setSpreadsheetColumns(result.columns);
        setModifiedData([]);
        setSpreadsheetKey(prevKey => prevKey + 1);
        setTimeout(() => {
          setShowSpreadsheet(true);
        }, 100);
      } catch (error) {
        console.error("Error processing API data:", error);
        toast.error(MESSAGES.DATA_LOAD_ERROR);
      }
    }
  }, [windowDimension, empConfigsData, processApiDataForSpreadsheet, showSpreadsheet]);

  return (
    <>
      <Header title="PT & LWF Configurations"/>
      <div className="page-content">
        <Container fluid>
          <Row>
            <Col lg={12}>
              <Card className="ps-card">
                <CardHeader className="ps-card-header">
                  <div className="d-flex align-items-center">
                    <div>
                      <h5 className="mb-0">{pageHeader || "Professional Tax & Labour Welfare Fund"}</h5>
                      {dateRange.start && dateRange.end && (
                        <div className="date-range-display me-3">
                          <small>
                            Showing data from{" "}
                            <strong>
                              {new Date(dateRange.start).toLocaleDateString()}
                            </strong>{" "}
                            to{" "}
                            <strong>
                              {new Date(dateRange.end).toLocaleDateString()}
                            </strong>
                          </small>
                        </div>
                      )}
                    </div>
                    <div className="ms-auto d-flex align-items-center">
                      {/* Save Button */}
                      <div className="ms-2">
                        <button
                          onClick={handleSave}
                          disabled={isSaving || modifiedData.length === 0}
                          className="ps-btn sm-btn primary-btn"
                          aria-label="Save changes"
                          style={{
                            opacity: isSaving || modifiedData.length === 0 ? 0.7 : 1,
                          }}
                        >
                          {(isSaving || isPending) && (
                            <Spinner size="sm" className="me-2" />
                          )}
                          Save
                        </button>
                      </div>
                    </div>
                  </div>
                </CardHeader>

                <CardBody>
                  <div className="ps-spredsheetTable table-responsive ps-table-wrapper position-relative ps-selection">
                    {isInitializing ? (
                      <Skeleton count={8} height={50} />
                    ) : 
                      !_.isEmpty(Object?.keys(empConfigsData || [])?.filter(key => key !== API_KEYS.ORG_CONFIG)) ? (
                        <Spreadsheet
                          ref={spreadsheetRef}
                          license={licenceKey}
                          editable={true}
                          onevent={globalEventHandler}
                          autoIncrement={false}
                          nestedHeaders={NESTED_HEADERS}
                          includeHeadersOnDownload={true}
                          contextMenu={() => { return false }}
                          className="jss_modern"
                        >
                          <Worksheet
                            key={spreadsheetKey}
                            freezeColumns="2"
                            data={spreadsheetData}
                            columns={spreadsheetColumns}
                            nestedHeaders={NESTED_HEADERS}
                            defaultColAlign="center"
                            search={false}
                            tableOverflow={true}
                            allowInsertRow={false}
                            allowInsertColumn={false}
                            allowDeletingRows={false}
                            rowResize={false}
                            minDimensions={[1, 1]}
                          />
                        </Spreadsheet>
                    ) : ( <NoDataFound /> )}
                  </div>
                </CardBody>
              </Card>
            </Col>
          </Row>
        </Container>
      </div>
    </>
  );
}

export default PTLWFConfigs;

