import { useDispatch, useSelector } from "react-redux";
import React, { useEffect, useState, useCallback, useMemo, useRef } from "react";
import { useLocation } from "react-router-dom";
import { getEmployeeConfigsData } from "../../store/payroll/actions";
import { saveEmployeeConfigsApi } from "../../helpers/awsapibackend_helper";
import { licenceKey } from "../../Components/constants/licence_key";
import { useMutation } from "@tanstack/react-query";
import { Container, Card, Row, Col, CardHeader, CardBody, Spinner } from "reactstrap";
import Header from "../../Layouts/Header";
import Skeleton from "react-loading-skeleton";
import "react-loading-skeleton/dist/skeleton.css";
import _ from "lodash";
import SpreadsheetContainer from "../../Components/Common/SpreadsheetContainer";
import { toast } from "react-toastify";
import { NoDataFound } from "../../Components/Common/NoDataFound";
import { Spreadsheet, Worksheet } from "@jspreadsheet/react";

const THEME = {
  ACTIVE_COLOR: "#039855",
  DEFAULT_COLOR: "#000000"
};

const SPREADSHEET_CONFIG = {
  SHEET_NAME: "PTLWFConfigs",
  FREEZE_COLUMNS: "2",
  COL_ALIGN: "center",
  REFRESH_DELAY: 1
};

const COLUMN_TYPES = {
  DROPDOWN: "dropdown",
  NUMERIC: "numeric",
  TEXT: "text"
};

const TOGGLE_OPTIONS = ["On", "Off"];

const MESSAGES = {
  VALIDATION_ERROR: "Validation failed: Please ensure all enabled PT and LWF entries have valid amounts.",
  NO_CHANGES: "No changes to save",
  SAVE_SUCCESS: "Configuration saved successfully",
  SAVE_ERROR: "Error saving configuration",
  DATA_LOAD_ERROR: "Error loading PT & LWF configuration data",
  UNEXPECTED_ERROR: "An unexpected error occurred"
};

const API_KEYS = {
  ORG_CONFIG: "org_config",
  CONFIG_ID: "statutory_compliance_id",
  ROW_ID: "row_id",
  CODE: "employee_code",
  EMPLOYEE_NAME: "employee_name",
  DEPARTMENT: "department",
  DESIGNATION: "designation",
  PT: "professional_tax",
  PT_AMOUNT: "pt_amount",
  LWF: "labour_welfare_fund",
  LWF_AMOUNT: "lwf_amount"
};

const NESTED_HEADERS = [
  [
    { title: "Employees", colspan: "4", frozen: false, align: "center" },
    { title: "Professional Tax", colspan: "2", frozen: true, align: "center" },
    { title: "Labour Welfare Fund", colspan: "2", frozen: true, align: "center" }, 
  ]
];

const columnNamesCache = new Map();
const getColumnName = (index) => {
  if (index === undefined || index < 0) return '';

  if (columnNamesCache.has(index)) {
    return columnNamesCache.get(index);
  }

  let name = "";
  let tempIndex = index;
  while (tempIndex >= 0) {
    name = String.fromCharCode((tempIndex % 26) + 65) + name;
    tempIndex = Math.floor(tempIndex / 26) - 1;
  }
  columnNamesCache.set(index, name);
  return name;
};

function PTLWFConfigs() {
  const location = useLocation();
  const dispatch = useDispatch();

  const [dateRange, setDateRange] = useState({ start: null, end: null });
  const [spreadsheetData, setSpreadsheetData] = useState([]);
  const [spreadsheetColumns, setSpreadsheetColumns] = useState([]);
  const [spreadsheetKey, setSpreadsheetKey] = useState(0);
  const [modifiedData, setModifiedData] = useState([]);
  const [isSaving, setIsSaving] = useState(false);
  const [showSpreadsheet, setShowSpreadsheet] = useState(true);
  const [pageHeader, setPageHeader] = useState(null);
  const [isStylesReady, setIsStylesReady] = useState(false);
  const [isSpreadsheetLoaded, setIsSpreadsheetLoaded] = useState(false);
  const isRestoringDropdownValueRef = useRef(false);
  const prevEmpConfigsDataRef = useRef(null);
  const spreadsheetRef = useRef(null);

  const { empConfigsData, isEmpConfigsloading } = useSelector((state) => ({
    empConfigsData: state.Payroll.empConfigsData,
    isEmpConfigsloading: state.Payroll.isEmpConfigsloading,
  }));

  const isInitializing = useMemo(() => {
    return isEmpConfigsloading || !showSpreadsheet || !isStylesReady;
  }, [isEmpConfigsloading, showSpreadsheet, isStylesReady]);

  const { mutate, isPending } = useMutation({
    mutationFn: saveEmployeeConfigsApi,
    onSuccess: (data) => {
      if (data?.statusCode === 200) {
        toast.success(data?.message || MESSAGES.SAVE_SUCCESS);
        if (dateRange.start && dateRange.end) {
          dispatch(
            getEmployeeConfigsData({
              effective_date_from: dateRange.start,
              effective_date_to: dateRange.end,
              config_name: 'pt_and_lwf'
            })
          );
        }
      } else {
        toast.error(data?.message || MESSAGES.SAVE_ERROR);
      }
      setIsSaving(false);
    },
    onError: (error) => {
      console.error("API Error:", error);
      toast.error(error?.message || MESSAGES.SAVE_ERROR);
      setIsSaving(false);
    },
  });

   //  dynamic window dimension detection start
     const [windowDimension, detectHW] = useState({
       innerWidth: window.innerWidth,
