          dayColumn: 140
        };
      } else if (screenWidth <= 1366) { // Small desktop
        return {
          empId: 50,
          empCode: 100,
          name: 130,
          department: 130,
          designation: 130,
          dayColumn: 150
        };
      } else if (screenWidth <= 1620) { // Standard desktop
        return {
          empId: 50,
          empCode: 100,
          name: 130,
          department: 130,
          designation: 130,
          dayColumn: 150
        };
      } else { // Large desktop/4K
        return {
          empId: 50,
          empCode: 100,
          name: 130,
          department: 130,
          designation: 130,
          dayColumn: 150
        };
      }
    }, [windowDimension]);

      // Dynamic CSS for jss_content jss_content_overflow class
    useEffect(() => {
      const calculateDynamicDimensions = () => {
        const screenWidth = windowDimension.innerWidth;
        const screenHeight = windowDimension.innerHeight;

        // Calculate dynamic height based on screen size and UI elements
        let dynamicHeight = screenHeight;

        // Account for common UI elements (header, footer, margins, etc.)
        const reservedHeight = 20; // Adjust this value based on your header/footer height

        // Define responsive height breakpoints
        if (screenHeight <= 600) { // Small screens
          dynamicHeight = screenHeight - 120;
        } else if (screenHeight <= 800) { // Medium screens
          dynamicHeight = screenHeight - 160;
        } else if (screenHeight <= 1080) { // Standard screens
          dynamicHeight = screenHeight - reservedHeight;
        } else { // Large screens
          dynamicHeight = screenHeight - 250;
        }

        // Ensure minimum height
        dynamicHeight = Math.max(dynamicHeight, 400);

        return { 
          width: screenWidth, 
          height: screenHeight - 120, 
          dynamicHeight: dynamicHeight - 120
        };
      };

      const updateJSSContentStyles = () => {
        const { width, height, dynamicHeight } = calculateDynamicDimensions();

        // Remove existing style if it exists
        const existingStyle = document.getElementById('jss-content-dynamic-styles');
        if (existingStyle) {
          existingStyle.remove();
        }

        // Create new style element
        const style = document.createElement('style');
        style.id = 'jss-content-dynamic-styles';
        style.textContent = `
