            border: 1px solid #f0f0f0 !important;
            border-color: #f0f0f0 !important;
            position: sticky !important;
            top: 0 !important;
            z-index: 9 !important;
          }

          .jss_modern .jss_row_header {
            background: var(--jss-header-background) !important;
            font-weight: 500 !important;
