import { useDispatch, useSelector } from "react-redux";
import React, { useEffect, useState, useCallback, useMemo, useRef } from "react";
import { useLocation } from "react-router-dom";
import { getEmployeeConfigsData } from "../../store/payroll/actions";
import { saveEmployeeConfigsApi } from "../../helpers/awsapibackend_helper";
import { licenceKey } from "../../Components/constants/licence_key";
import { useMutation } from "@tanstack/react-query";
import { Container, Card, Row, Col, CardHeader, CardBody, Spinner } from "reactstrap";
import Header from "../../Layouts/Header";
import Skeleton from "react-loading-skeleton";
import "react-loading-skeleton/dist/skeleton.css";
import _ from "lodash";
import SpreadsheetContainer from "../../Components/Common/SpreadsheetContainer";
import { toast } from "react-toastify";
import { NoDataFound } from "../../Components/Common/NoDataFound";
import { Spreadsheet, Worksheet } from "@jspreadsheet/react";

const THEME = {
  ACTIVE_COLOR: "#039855",
  DEFAULT_COLOR: "#000000"
};

const SPREADSHEET_CONFIG = {
  SHEET_NAME: "PTLWFConfigs",
  FREEZE_COLUMNS: "2",
  COL_ALIGN: "center",
  REFRESH_DELAY: 1
};

const COLUMN_TYPES = {
  DROPDOWN: "dropdown",
  NUMERIC: "numeric",
  TEXT: "text"
};

const TOGGLE_OPTIONS = ["On", "Off"];

const MESSAGES = {
  VALIDATION_ERROR: "Validation failed: Please ensure all enabled PT and LWF entries have valid amounts.",
  NO_CHANGES: "No changes to save",
  SAVE_SUCCESS: "Configuration saved successfully",
  SAVE_ERROR: "Error saving configuration",
  DATA_LOAD_ERROR: "Error loading PT & LWF configuration data",
  UNEXPECTED_ERROR: "An unexpected error occurred"
};

