      { title: "Bonus Amount", width: columnWidths.dayColumn, type: COLUMN_TYPES.NUMERIC, decimal: '.', mask: '#,##0.00', align: 'right', wrap: true },
      { title: "Bonus Reason", width: columnWidths.dayColumn, wrap: true },
