      { title: "Incentive", width: columnWidths.dayColumn, type: COLUMN_TYPES.DROPDOWN, source: TOGGLE_OPTIONS, wrap: true },
      { title: "Incentive Amount", width: columnWidths.dayColumn, type: COLUMN_TYPES.NUMERIC, decimal: '.', mask: '#,##0.00', align: 'right', wrap: true },
