    } else if (screenWidth <= 1366) { // Small desktop
      return {
        empId: 50,
        empCode: 100,
        name: 130,
        department: 130,
        designation: 130,
        dayColumn: 150
      };
    } else if (screenWidth <= 1620) { // Standard desktop
      return {
        empId: 50,
        empCode: 100,
        name: 130,
        department: 130,
