          background-color: var(--jss-hover-background) !important;
        }

        .jss_modern .jss_cell.jss_selected {
          background-color: var(--jss-selected-background) !important;
          border-color: #2196f3 !important;
        }

        .jss_modern .jss_nested {
          background: #E3F1FF !important;
          border: 1px solid #f0f0f0 !important;
          border-color: #f0f0f0 !important;
          font-weight: 600 !important;
          color: #495057 !important;
          position: sticky !important;
          top: 0 !important;
          z-index: 10 !important;
        }

        /* Fix for content not overlapping with sticky headers */
        .jss_modern .jss_content {
          position: relative !important;
          z-index: 1 !important;
        }

        .jss_modern .jss_cell {
          position: relative !important;
          z-index: 1 !important;
        }

        @media (min-width: 1680px) {
          .jss_content.jss_content_overflow,
          .jss_content.jss_content_overflow.jss_modern {
            max-width: 92vw !important;
          }
        }
        @media (max-width: 1366px) {
          .jss_content.jss_content_overflow,
          .jss_content.jss_content_overflow.jss_modern {
            max-width: 88.9vw ;
          }
        }

        @media (max-width: 768px) {
          .jss_content.jss_content_overflow,
          .jss_content.jss_content_overflow.jss_modern {
            width: 88.9vw !important;
            height: ${Math.max(dynamicHeight * 0.8, 350)}px !important;
            max-height: ${height * 0.85}px !important;
          }
        }

        @media (max-width: 480px) {
          .jss_content.jss_content_overflow,
          .jss_content.jss_content_overflow.jss_modern {
            width: 98vw !important;
            height: ${Math.max(dynamicHeight * 0.75, 300)}px !important;
            max-height: ${height * 0.8}px !important;
          }
        }

        @media (min-height: 1200px) {
          .jss_content.jss_content_overflow,
          .jss_content.jss_content_overflow.jss_modern {
            height: ${Math.min(dynamicHeight, height * 0.85)}px !important;
          }
        }

        @media (max-height: 600px) {
          .jss_content.jss_content_overflow,
          .jss_content.jss_content_overflow.jss_modern {
            height: ${Math.max(height - 100, 250)}px !important;
          }
        }
      `;

      document.head.appendChild(style);
    };

    // Update styles when component mounts and when window dimension changes
    updateJSSContentStyles();

    // Set styles as ready after applying
    setIsStylesReady(true);

    // Cleanup function
    return () => {
