    const [filteringData, setFilteringData] = useState(filtering);
    const [wasDataEmpty, setWasDataEmpty] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            const currentScrollLeft = tableScrollRef.current.scrollLeft;
            if (currentScrollLeft !== lastScrollLeft.current) {
                lastScrollLeft.current = currentScrollLeft;

                // Close all dropdowns
                setSortPopupOpen(prev => {
                    const newState = { ...prev };
                    for (let key in newState) {
                        newState[key] = false;
                    }
                    return newState;
                });

                // Also optionally close sort icon state
                setSortIconOpen(prev => {
                    const newIconState = { ...prev };
                    for (let key in newIconState) {
                        newIconState[key] = false;
                    }
                    return newIconState;
                });
            }
        };

        const scrollContainer = tableScrollRef.current;
        if (scrollContainer) {
            scrollContainer.addEventListener("scroll", handleScroll);
        }

        return () => {
            if (scrollContainer) {
                scrollContainer.removeEventListener("scroll", handleScroll);
            }
        };
    }, []);

    // // Function to handle clear all checkbox
    // const clearAllSearch = () => {
    //     setSelectedValues({});
    // };

    // // Apply filters and get filtered data
    // const setfilteredData = useMemo(() => {
    //     let filtered = [...data];
    //     setFilteredData(filtered);
    //     clearAllSearch();
    // }, [data]);

    // Custom sorting option for proper data sorting
    const customSort = (rowA, rowB, columnId) => {
        const normalize = (value) => {
            if (value === null || value === undefined) {
                return ''; // Treat null and undefined as empty strings
            }
            if (typeof value === 'string') {
                return value.trim().replace(/[^a-zA-Z0-9]/g, '');
            }
            return value;
        };

        // Convert date in dd-mm-yyyy format to timestamp
        const parseDateToTimestamp = (value) => {
            const [day, month, year] = value.split('-').map(Number);
            return new Date(year, month - 1, day).getTime(); // Convert to timestamp
        };

        // Helper function to check if a value is a valid date in dd-mm-yyyy format
        const isDate = (value) => {
            const regex = /^\d{2}-\d{2}-\d{4}$/;
            return regex.test(value);
        };

        // Function to check if value is null or undefined
        const isNullOrUndefined = (value) => {
            return value === null || value === undefined;
        };

        // Handle null values first
        if (isNullOrUndefined(rowA.values[columnId]) && !isNullOrUndefined(rowB.values[columnId])) {
            return 1; // rowA should come after rowB
        }

        if (!isNullOrUndefined(rowA.values[columnId]) && isNullOrUndefined(rowB.values[columnId])) {
            return -1; // rowA should come before rowB
        }

        // Handle dates next
        if (isDate(rowA.values[columnId]) && isDate(rowB.values[columnId])) {
            const timestampA = parseDateToTimestamp(rowA.values[columnId]);
            const timestampB = parseDateToTimestamp(rowB.values[columnId]);
            return timestampA - timestampB;
        }

        const a = normalize(rowA.values[columnId]);
        const b = normalize(rowB.values[columnId]);

        // Helper function to check if a value is numeric
        const isNumeric = (value) => {
            return !isNaN(value) && !isNaN(parseFloat(value));
        };

        // Convert numeric values stored as strings to numbers for comparison
        const parseValue = (value) => {
            return isNumeric(value) ? parseFloat(value) : value;
        };

        const parsedA = parseValue(a);
        const parsedB = parseValue(b);

        // If both values are numeric, compare numerically
        if (isNumeric(parsedA) && isNumeric(parsedB)) {
            return parsedA - parsedB;
        }

        // If both values are strings, compare alphabetically
        if (typeof parsedA === 'string' && typeof parsedB === 'string') {
            return parsedA.localeCompare(parsedB);
        }

        // If one value is numeric and the other is a string, sort numbers before strings
        if (isNumeric(parsedA) && typeof parsedB === 'string') {
            return -1;
        }

        if (typeof parsedA === 'string' && isNumeric(parsedB)) {
            return 1;
        }

        // If values contain special characters, sort them last
        const specialCharCheck = (value) => {
            return typeof value === 'string' && /[^a-zA-Z0-9]/.test(value);
        };

        if (specialCharCheck(parsedA) && !specialCharCheck(parsedB)) {
            return 1;
        }

        if (!specialCharCheck(parsedA) && specialCharCheck(parsedB)) {
            return -1;
        }

        return (parsedA > parsedB) ? 1 : (parsedA < parsedB) ? -1 : 0;
    };

