        });
    };

    const pageCount = pageOptions.length; // Calculate pageCount from pageOptions

    const firstRecordIndex = pageIndex * pageSize + 1;
    const lastRecordIndex = Math.min((pageIndex + 1) * pageSize, filteredData.length);

    //search code

    // Memoize unique column values for the popup
    const uniqueColumnValues = useMemo(() => {
        const uniqueValues = {};
        columns.forEach((column) => {
        const accessor = column.accessor;
        uniqueValues[accessor] = new Set();
        data.forEach((row) => {
