            : new Set([...updatedValues[columnAccessor], value])
            : new Set([value]);
        return updatedValues;
        });
    };

    const handleSearchSubmit = useRef();

    // Function to handle form submission in popup
    handleSearchSubmit.current = (selected_column = false, resetPage = true) => {
        // Apply filters based on selected values


        const newFilteredData = data.filter((row) => {
        return Object.entries(selectedValues).every(([columnAccessor, values]) => {
