                                    </td>
                                </tr>
                            </tbody>
                            :
                            ''
                        }
                </Table>
            </div>

            {!isDashboardUrl && (flag === 1) && filteredData.length > pageSize ?
                <div className="ps-pagination">
                    <div className="ps-page-total">
                        <p>
                            Showing <span>{firstRecordIndex}</span> to <span>{lastRecordIndex}</span> of <span>{filteredData.length}</span> Results
                        </p>
                    </div>
                    <div className="pagination-wrap hstack gap-2">
                        <Link
                            className={classnames("page-item ps-pagination-prev", (pageIndex === 0) ? 'disabled' : '')}
                            to="#"
                            onClick={() => { previousPage(); (onPageChange !== false && onPageChange()); }}
                        >
                            Previous
                        </Link>
                        <ul className="pagination ps-listjs-pagination mb-0">
                            {Array.from({ length: Math.min(pageCount, 5) }, (_, index) => {
                                let pageOffset = Math.floor(5 / 2);
                                let startPage = Math.max(0, Math.min(pageIndex - pageOffset, pageCount - 5));
                                let endPage = Math.min(pageCount, startPage + 5);
                                return (
                                    index < endPage - startPage &&
                                    <li key={startPage + index} onClick={() => { gotoPage(startPage + index); (onPageChange !== false && onPageChange()); }} className={(pageIndex === startPage + index) ? 'active' : ''}>
                                        <Link className="page" to="#">
                                            {startPage + index + 1}
                                        </Link>
                                    </li>
                                );
                            })}
                        </ul>
                        <Link
                            className={classnames("page-item ps-pagination-next", (pageIndex === pageCount - 1) ? 'disabled' : '')}
                            to="#"
                            onClick={() => { nextPage(); (onPageChange !== false && onPageChange()); }}
                        >
                            Next
                        </Link>
                    </div>
                </div>
                : ''
            }

        </>
    );
};

export default MyCustomTable;

import React, { useState, useMemo, useEffect, useRef } from 'react';
import { useTable, useSortBy, usePagination, useFilters } from 'react-table';
import { Link ,useLocation } from 'react-router-dom';
import classnames from "classnames";
import dropdownClose from "../../assets/images/svg/dropdownClose.svg"
import dropdownOpen from "../../assets/images/svg/dropdownOpen.svg"
import Dropdown from 'react-bootstrap/Dropdown';
import { Table } from "reactstrap";
import searchicon from "../../assets/images/svg/searchicon.svg"
import { DropDownIcon, ShortingIcon } from './AllSvgIcons';

const MyCustomTable = ({ columns, data, onPageChange = false, autoResetPage = false, setFilteredState = false, filtering, flag = 1 ,tableHeight ,tableName ,showFooter = false , colspan , isDefaultDescending = true , isDashboard = false}) => {
    const location = useLocation();
    const tableScrollRef = useRef(null);
    const lastScrollLeft = useRef(0);
    const isDashboardUrl = isDashboard;
    // const isDashboardUrl = location.pathname === '/dashboard';
    const [filterInput, setFilterInput] = useState('');
    const [selectedValues, setSelectedValues] = useState({});
    const [filteredData, setFilteredData] = useState(data);
    const [searchValue, setSearchValue] = useState('');
    const [filteringData, setFilteringData] = useState(filtering);
    const [wasDataEmpty, setWasDataEmpty] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            const currentScrollLeft = tableScrollRef.current.scrollLeft;
            if (currentScrollLeft !== lastScrollLeft.current) {
                lastScrollLeft.current = currentScrollLeft;

                // Close all dropdowns
                setSortPopupOpen(prev => {
                    const newState = { ...prev };
                    for (let key in newState) {
                        newState[key] = false;
                    }
                    return newState;
                });

                // Also optionally close sort icon state
                setSortIconOpen(prev => {
                    const newIconState = { ...prev };
                    for (let key in newIconState) {
                        newIconState[key] = false;
                    }
                    return newIconState;
                });
            }
        };

        const scrollContainer = tableScrollRef.current;
        if (scrollContainer) {
            scrollContainer.addEventListener("scroll", handleScroll);
        }

        return () => {
            if (scrollContainer) {
                scrollContainer.removeEventListener("scroll", handleScroll);
            }
        };
    }, []);

    // // Function to handle clear all checkbox
    // const clearAllSearch = () => {
    //     setSelectedValues({});
    // };

    // // Apply filters and get filtered data
    // const setfilteredData = useMemo(() => {
    //     let filtered = [...data];
    //     setFilteredData(filtered);
    //     clearAllSearch();
    // }, [data]);

    // Custom sorting option for proper data sorting
    const customSort = (rowA, rowB, columnId) => {
        const normalize = (value) => {
            if (value === null || value === undefined) {
                return ''; // Treat null and undefined as empty strings
            }
            if (typeof value === 'string') {
                return value.trim().replace(/[^a-zA-Z0-9]/g, '');
            }
            return value;
        };

        // Convert date in dd-mm-yyyy format to timestamp
        const parseDateToTimestamp = (value) => {
            const [day, month, year] = value.split('-').map(Number);
            return new Date(year, month - 1, day).getTime(); // Convert to timestamp
        };

        // Helper function to check if a value is a valid date in dd-mm-yyyy format
        const isDate = (value) => {
            const regex = /^\d{2}-\d{2}-\d{4}$/;
            return regex.test(value);
        };

        // Function to check if value is null or undefined
        const isNullOrUndefined = (value) => {
            return value === null || value === undefined;
        };

        // Handle null values first
        if (isNullOrUndefined(rowA.values[columnId]) && !isNullOrUndefined(rowB.values[columnId])) {
            return 1; // rowA should come after rowB
        }

        if (!isNullOrUndefined(rowA.values[columnId]) && isNullOrUndefined(rowB.values[columnId])) {
            return -1; // rowA should come before rowB
        }

        // Handle dates next
        if (isDate(rowA.values[columnId]) && isDate(rowB.values[columnId])) {
            const timestampA = parseDateToTimestamp(rowA.values[columnId]);
            const timestampB = parseDateToTimestamp(rowB.values[columnId]);
            return timestampA - timestampB;
        }

        const a = normalize(rowA.values[columnId]);
        const b = normalize(rowB.values[columnId]);

        // Helper function to check if a value is numeric
        const isNumeric = (value) => {
            return !isNaN(value) && !isNaN(parseFloat(value));
        };

        // Convert numeric values stored as strings to numbers for comparison
        const parseValue = (value) => {
            return isNumeric(value) ? parseFloat(value) : value;
        };

        const parsedA = parseValue(a);
        const parsedB = parseValue(b);

        // If both values are numeric, compare numerically
        if (isNumeric(parsedA) && isNumeric(parsedB)) {
            return parsedA - parsedB;
        }

        // If both values are strings, compare alphabetically
        if (typeof parsedA === 'string' && typeof parsedB === 'string') {
            return parsedA.localeCompare(parsedB);
        }

        // If one value is numeric and the other is a string, sort numbers before strings
        if (isNumeric(parsedA) && typeof parsedB === 'string') {
            return -1;
        }

        if (typeof parsedA === 'string' && isNumeric(parsedB)) {
            return 1;
        }

        // If values contain special characters, sort them last
        const specialCharCheck = (value) => {
            return typeof value === 'string' && /[^a-zA-Z0-9]/.test(value);
        };

        if (specialCharCheck(parsedA) && !specialCharCheck(parsedB)) {
            return 1;
        }

        if (!specialCharCheck(parsedA) && specialCharCheck(parsedB)) {
            return -1;
        }

        return (parsedA > parsedB) ? 1 : (parsedA < parsedB) ? -1 : 0;
    };

    // Customize column default settings (generelize for all)
    const modifiedColumns = useMemo(() => {
        return columns ? columns.map(column => ({
            ...column,
            sortType: customSort,
        })) : [];
    }, [columns]);

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        page,
        prepareRow,
        setFilter,
        state: { pageIndex, pageSize },
        gotoPage,
        nextPage,
        previousPage,
        canNextPage,
        canPreviousPage,
        pageOptions,
        setPageSize,
        footerGroups 
    } = useTable(
        {
            columns: modifiedColumns,
            data: (isDashboardUrl && filtering) ? filteringData : filteredData,
            autoResetPage: false,
            autoResetSortBy: false,
            initialState: {
                pageIndex: 0, pageSize: flag === 2 ? 500 : 10, sortBy: isDefaultDescending ? [
                    {
                        desc: true,
                    },
                ] : []
            }, // Initial page index and page size
        },
        useFilters,
        useSortBy,
        usePagination, // Add usePagination hook
    );

    // State to manage the sorting options popup
    const [sortPopupOpen, setSortPopupOpen] = useState({});
    const [sortIconOpen, setSortIconOpen] = useState({});

    // State to manage the sort direction
    const [sortDirection, setSortDirection] = useState({});

    // Function to handle sorting option click
    // const handleSortOptionClick = columnId => {
    //     setSortPopupOpen(prevState => ({ ...prevState, [columnId]: !prevState[columnId] }));
    //     setSortIconOpen(prevState => ({ ...prevState, [columnId]: !prevState[columnId] }));
    // };

    const handleSortOptionClick = columnId => {
        setSortPopupOpen(prevState => {
          const isOpen = !!prevState[columnId];
          // Reset all dropdown states to false
          const newState = Object.keys(prevState).reduce((acc, key) => {
            acc[key] = false;
            return acc;
          }, {});
          // Toggle the clicked dropdown
          newState[columnId] = !isOpen;
          return newState;
        });

        setSortIconOpen(prevState => {
          const isOpen = !!prevState[columnId];
          const newState = Object.keys(prevState).reduce((acc, key) => {
            acc[key] = false;
            return acc;
          }, {});
          newState[columnId] = !isOpen;
          return newState;
        });
    };

    const pageCount = pageOptions.length; // Calculate pageCount from pageOptions

    const firstRecordIndex = pageIndex * pageSize + 1;
    const lastRecordIndex = Math.min((pageIndex + 1) * pageSize, filteredData.length);

    //search code

    // Memoize unique column values for the popup
    const uniqueColumnValues = useMemo(() => {
        const uniqueValues = {};
        columns.forEach((column) => {
        const accessor = column.accessor;
        uniqueValues[accessor] = new Set();
        data.forEach((row) => {
            const value = row[accessor];
            if (value !== undefined && value !== '') {
                if (typeof value === 'string') {
                    value?.split(',').forEach((val) => uniqueValues[accessor].add(val.trim()));
                }
            }
        });
        });
        return uniqueValues;
    }, [columns, data]);

    // Function to handle checkbox selection in popup
    const handleCheckboxChange = (columnAccessor, value) => {
        setSelectedValues((prevSelectedValues) => {
        const updatedValues = { ...prevSelectedValues };
        updatedValues[columnAccessor] = updatedValues[columnAccessor]
            ? updatedValues[columnAccessor].has(value)
            ? new Set([...updatedValues[columnAccessor]].filter((v) => v !== value))
            : new Set([...updatedValues[columnAccessor], value])
            : new Set([value]);
        return updatedValues;
        });
    };

    const handleSearchSubmit = useRef();

    // Function to handle form submission in popup
    handleSearchSubmit.current = (selected_column = false, resetPage = true) => {
        // Apply filters based on selected values


        const newFilteredData = data.filter((row) => {
        return Object.entries(selectedValues).every(([columnAccessor, values]) => {
            const cellValue = row[columnAccessor];
            if (cellValue === undefined) return false;
            const cellValuesArray = cellValue?.split(',').map((val) => val.trim());
            return values.size === 0 || cellValuesArray?.some((val) => values.has(val));
        });
        });

        if(isDashboardUrl){
            setFilteringData(newFilteredData.slice(0,7));
        }
        else if(!isDashboardUrl){
            setFilteredData(newFilteredData);
        }

        if(selected_column !== false) {
            setSortPopupOpen(prevState => ({ ...prevState, [selected_column]: !prevState[selected_column] }));
            setSortIconOpen(prevState => ({ ...prevState, [selected_column]: !prevState[selected_column] }));
        }
        setSearchValue('');
        // go to page 0 for new search results
        if(resetPage === true) {
            gotoPage(0);
        }
    }

    // on data refresh :- set filter values and table data as it was before
    useEffect(() => {
        // If data is empty and was previously not empty, reset filteredData to avoid infinite loop
        if (data.length === 0 && !wasDataEmpty) {
            setFilteredData([]);
            setWasDataEmpty(true);
        } else if (data.length > 0) {
            // If data is not empty and was previously empty, update filteredData
            handleSearchSubmit.current(false, false);
            setWasDataEmpty(false);
        }
    }, [data, wasDataEmpty]);


    // set filtered data for parent component
    useEffect(() => {
        if(setFilteredState !== false) {
            setFilteredState(filteredData)
        }
    }, [filteredData, setFilteredState])

    const handleCancelSearch = (selected_column) => {
        setSortPopupOpen(prevState => ({ ...prevState, [selected_column]: !prevState[selected_column] }));
        setSortIconOpen(prevState => ({ ...prevState, [selected_column]: !prevState[selected_column] }));
        setSearchValue('');
    }

    // Filter unique column values based on search input
    const filteredUniqueColumnValues = useMemo(() => {
        const filteredValues = {};
        Object.entries(uniqueColumnValues).forEach(([columnAccessor, values]) => {
        const filtered = Array.from(values).filter((value) => {
            const stringValue = String(value).toLowerCase();
            return stringValue.includes(searchValue.toLowerCase());
        });
        filteredValues[columnAccessor] = new Set(filtered);
        });
        return filteredValues;
    }, [uniqueColumnValues, searchValue]);

    // Function to handle search input change in popup
    const handleSearchChange = (e) => {
        setSearchValue(e.target.value);
    };

    // Function to handle select all checkbox
    const handleSelectAll = (columnAccessor) => {
        setSelectedValues((prevSelectedValues) => {
        const updatedValues = { ...prevSelectedValues };
        updatedValues[columnAccessor] = new Set(filteredUniqueColumnValues[columnAccessor]);
        return updatedValues;
        });
    };

    // Function to handle clear all checkbox
    const handleClearAll = (columnAccessor) => {
        setSelectedValues((prevSelectedValues) => {
        const updatedValues = { ...prevSelectedValues };
        updatedValues[columnAccessor] = new Set();
        return updatedValues;
        });
    };

    //  dynamic scrollbar height start
   const [windowDimension, detectHW]= useState(window.innerHeight)
   const detectSize = ()=>{
    detectHW( window.innerHeight)
    }
    useEffect(() => {
        window.addEventListener('resize', detectSize)
        return()=>{
            window.removeEventListener('resize', detectSize)
        }
    }, [windowDimension])
    //  dynamic scrollbar height end

    return (
        <>
            <div ref={tableScrollRef} className={(filteredData.length === 0 || (filtering && filteringData.length === 0)) ? "ps-table-wrapper table-min-height" :"ps-table-wrapper table-min-height"}   style={tableHeight ? { maxHeight: windowDimension - tableHeight } : {}}>
                <Table {...getTableProps()} className={`${tableName} table-nowrap ps-table`}>
                        <thead>
                            {headerGroups.map((headerGroup) => {
                                const { key, ...headerGroupProps } = headerGroup.getHeaderGroupProps();
                                return (
                                <tr key={key} {...headerGroupProps}>
                                    {headerGroup.headers.map((column) => {
                                    const { key, ...headerProps } = column.getHeaderProps();
                                    return (
                                        <th key={key} {...headerProps} className="position-relative cursor-pointer">
                                        {column.render("Header")}
                                        {/* {column.sortable && column.Filter !== false && data.length > 0 && ( */}
                                        {column.sortable && data.length > 0 && (
                                            <Dropdown autoClose="inside" className="d-inline-block" onToggle={() => handleSortOptionClick(column.id)} show={sortPopupOpen[column.id]}>
                                                <Dropdown.Toggle className="ps-custom-table-fil ms-2" id={`dropdown-basic-${column.id}`}>
                                                    <DropDownIcon clsname="ps-DropDownIconTable"/>
                                                </Dropdown.Toggle>

                                                <Dropdown.Menu >
                                                    <div className="card-header ps-rounded-top-rl ps-border-bottom d-flex align-items-center">
                                                        <p className="paragraph-sm-1 cursor-pointer d-flex align-items-center"
                                                            onClick={() => { const isDesc = column.isSortedDesc ?? false; column.toggleSortBy(!isDesc);
                                                                setSortIconOpen(prevState => ({ ...prevState, [column.id]: false }));
                                                            }} > <ShortingIcon/> <span className='ms-2 paragraph-xsm-2'>Sort  {column.isSortedDesc ? 'A-Z' : 'Z-A'}</span>

                                                        </p>
                                                    </div>

                                                    <div className="card-body pt-2">
                                                        <div className="d-flex mb-2">
                                                            <p className="ps-text-primary" onClick={() => handleSelectAll(column.id)}><u>Select All</u></p>
                                                            <p className="ps-3 ps-text-primary" onClick={() => handleClearAll(column.id)}><u>Clear</u></p>
                                                        </div>
                                                        <div className="position-relative search-dropdown-wrap">
                                                            <img src={searchicon} alt="Search Icon" className="img-fluid ps-2" />
                                                            <input name="" placeholder="Search" type="text" onChange={handleSearchChange} className="form-control  form-control ps-rounded-7" value={searchValue} />
                                                        </div>
                                                        <div className="mt-3 ct-emp-shit-assign ps-filter-scroll-y ">
                                                            {filteredUniqueColumnValues[column.id] &&
                                                                Array.from(filteredUniqueColumnValues[column.id]).map((value) => {

                                                                    const selectedColumnValues = selectedValues[column.id] || new Set();

                                                                    return (<div className="form-check mb-2" key={value}>
                                                                        <input className="form-check-input ct-checkbox" type="checkbox" id={'chk_custom_filter'+value} value={value} onChange={() => handleCheckboxChange(column.id, value)} checked={selectedColumnValues.has(value)} />
                                                                        <label className="form-check-label ps-2 fw-400" htmlFor={'chk_custom_filter'+value}>{value}</label>
                                                                    </div>)
                                                                }
                                                            )}
                                                        </div>
                                                    </div>
                                                    <div className="card-footer text-end ps-rounded-btm-rl ps-border-top">
                                                        <button type="button" className="ps-btn sm-btn grey-outline-btn" onClick={() => handleCancelSearch(column.id)}>Cancel</button>
                                                        <button type="button" className="ps-btn sm-btn primary-btn ms-2" onClick={() => handleSearchSubmit.current(column.id)}>Save</button>
                                                    </div>
                                                </Dropdown.Menu>
                                            </Dropdown>
                                        )}
                                        </th>
                                    );
                                    })}
                                </tr>
                                );
                            })}
                        </thead>
                        <tbody {...getTableBodyProps()}>
                        {page.map((row) => {
                            prepareRow(row);
                            const { key, ...rowProps } = row.getRowProps();
                            return (
                            <tr key={key} {...rowProps}>
                                {row.cells.map((cell) => {
                                const { key, ...cellProps } = cell.getCellProps();
                                const className = cell.column.classNameString ? cell.column.classNameString(row.original) : "";
                                return (
                                    <td key={key} className={className} {...cellProps}>
                                    {cell.render("Cell")}
                                    </td>
                                );
                                })}
                            </tr>
                            );
                        })}
                        </tbody>

                        {/* Table Footer */}
                        {showFooter && (
                        <tfoot>
                            {footerGroups.map((footerGroup) => {
                            const { key, ...footerGroupProps } = footerGroup.getFooterGroupProps();
                            return (
                                <tr key={key} {...footerGroupProps}>
                                {footerGroup.headers.map((column) => {
                                    const { key, ...footerProps } = column.getFooterProps();
                                    return (
                                    <td key={key} {...footerProps}>
                                        {column.render("Footer")}
                                    </td>
                                    );
                                })}
                                </tr>
                            );
                            })}
                        </tfoot>
                        )}

                        {(filteredData.length === 0 || (filtering && filteringData.length === 0))
                            ?
                            <tbody>
