                                                        <path d="M55.0586 57.8359C60.8198 57.8359 65.4902 53.1655 65.4902 47.4043C65.4902 41.6431 60.8198 36.9727 55.0586 36.9727C49.2974 36.9727 44.627 41.6431 44.627 47.4043C44.627 53.1655 49.2974 57.8359 55.0586 57.8359Z" stroke="#91CCF2" strokeWidth="2.5" strokeMiterlimit="10" strokeLinecap="round" strokeLinejoin="round"/>
