import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { toast } from "react-toastify";
import { getEmployees, saveEmployeeWeekoffApi, saveBulkEmployeeWeekoffApi } from "../helpers/awsapibackend_helper";
import { EMPLOYEE_WEEKOFFS, SAVE_WEEKOFF, BULK_SAVE_WEEKOFF } from "../reactQueryKeys/weekoffQueryKeys";

// Fetch employees with weekoff data
export const useEmployeeWeekoffs = () => {
  return useQuery({
    queryKey: [EMPLOYEE_WEEKOFFS],
    queryFn: () => getEmployees({ method: 'employee_list_with_weekoff' }),
    select: (data) => data?.data || []
  });
};

// Save individual employee weekoff
export const useSaveWeekoff = () => {
  const queryClient = useQueryClient();

  return useMutation({
    mutationKey: [SAVE_WEEKOFF],
    mutationFn: saveEmployeeWeekoffApi,
    onSuccess: () => {
      queryClient.invalidateQueries([EMPLOYEE_WEEKOFFS]);
      toast.success("Employee Weekoff Saved Successfully");
    },
    onError: () => {
      toast.error("Problem Saving Data");
    }
  });
};

// Save bulk employee weekoff
export const useBulkSaveWeekoff = () => {
  const queryClient = useQueryClient();

  return useMutation({
    mutationKey: [BULK_SAVE_WEEKOFF],
    mutationFn: async (data) => {
      const obj = JSON.parse(sessionStorage.getItem("authUser"));
       const response = await saveBulkEmployeeWeekoffApi({
        ho_id: sessionStorage.getItem('organization_id'),
        employee_ids: data.employeeIds,
        day: data.day,
        occasional_weekoff_days: data.occasionalWeekoffDays,
        username: obj.data.username,
      });
      if (!response || response.status !== 1) {
        throw new Error("Failed to save bulk weekoff data");
      }
      return response;
    },
    onSuccess: () => {
      queryClient.invalidateQueries([EMPLOYEE_WEEKOFFS]);
      toast.success("Bulk Weekoff Saved Successfully");
    },
    onError: () => {
      toast.error("Problem Saving Bulk Data");
    }
  });
};

