                  weekoffData={{
                    employeeId: employee.id,
                    employeeName: employee.name,
                    shiftIds: employee.shift_ids?.split(",") || [],
                    day: dayIndex + 1,
                  }}
                />
              </Dropdown>
            </div>
          )
        }

        return (
          <div
            key={popupId + dayIndex}
            // onClick={handleClick}
            style={{ cursor: cursorStyle }}
          >
            {renderDay()}
          </div>
        );
      }
    });

    return [
      {
        Header: <div className="ps-checkbox no-text">
          <input 
            type="checkbox" 
            id="checkall" 
            checked={isSelectAllChecked}
            onChange={(e) => handleSelectAll(e.target.checked)}
            name='select_emp' 
          />
          <label htmlFor="checkall"></label>
        </div>,
        accessor: "select_emp",
        Filter: false,
        Cell: (cellProps) => {
          const { original } = cellProps.row;
          const employeeId = original.id;
          const uniqueId = `emp_${employeeId}`;
          return (
            <div className="ps-checkbox no-text">
              <input 
                type="checkbox" 
                id={uniqueId}
                checked={selectedEmployees.has(employeeId)}
                onChange={(e) => handleEmployeeSelect(employeeId, e.target.checked)}
                name='select_emp' 
              />
              <label htmlFor={uniqueId}></label>
            </div>
          );
        },
      },
      {
        Header: "Name",
        accessor: "name",
        sortable: false,
        Cell: ({ row }) => (
          <div>
            {row.original.name}
            <EmployeeTag tag={row.original.employee_status} />
          </div>
        ),
      },
      {
        Header: "Department",
        accessor: "department",
        sortable: false,
        Cell: ({ row }) => {
          return (
            <div>{row.original.department}</div>
          )
        },
      },
      ...Object.values(getDayName).map((day, index) => getDayColumn(day, index))
    ];
  }, [openDropdownId, closeDropDown, handleDropdownClick, selectedEmployees, isSelectAllChecked, handleEmployeeSelect, handleSelectAll]);

  document.title = "Weekoff | Petpooja Payroll";

  return (
    <React.Fragment>
      <Header title="Week off Assignment" />
      <div className="page-content">
        <div className="container-fluid">
          <div className="row">
            <div className="col-lg-12">
              <div className="card week-off-wrapper ps-border ps-rounded-10 shadow-none">
                <div className="card-header ps-border-bottom ps-rounded-top-rl px-4">
